import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-188 Defrauding hotels, motels, campgrounds, boardinghouses, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-188.md","filePath":"vacode/18.2-188.md"}'),a={name:"vacode/18.2-188.md"},n=r('<h1 id="§-18-2-188-defrauding-hotels-motels-campgrounds-boardinghouses-etc" tabindex="-1">§ 18.2-188 Defrauding hotels, motels, campgrounds, boardinghouses, etc. <a class="header-anchor" href="#§-18-2-188-defrauding-hotels-motels-campgrounds-boardinghouses-etc" aria-label="Permalink to &quot;§ 18.2-188 Defrauding hotels, motels, campgrounds, boardinghouses, etc.&quot;">​</a></h1><p>It shall be unlawful for any person, without paying therefor, and with the intent to cheat or defraud the owner or keeper to:</p><ol><li><p>Put up at a hotel, motel, campground or boardinghouse;</p></li><li><p>Obtain food from a restaurant or other eating house;</p></li><li><p>Gain entrance to an amusement park; or</p></li><li><p>Without having an express agreement for credit, procure food, entertainment or accommodation from any hotel, motel, campground, boardinghouse, restaurant, eating house or amusement park.</p></li></ol><p>It shall be unlawful for any person, with intent to cheat or defraud the owner or keeper out of the pay therefor to obtain credit at a hotel, motel, campground, boardinghouse, restaurant or eating house for food, entertainment or accommodation by means of any false show of baggage or effects brought thereto.</p><p>It shall be unlawful for any person, with intent to cheat or defraud, to obtain credit at a hotel, motel, campground, boardinghouse, restaurant, eating house or amusement park for food, entertainment or accommodation through any misrepresentation or false statement.</p><p>It shall be unlawful for any person, with intent to cheat or defraud, to remove or cause to be removed any baggage or effects from a hotel, motel, campground, boardinghouse, restaurant or eating house while there is a lien existing thereon for the proper charges due from him for fare and board furnished.</p><p>Any person who violates any provision of this section is, if the value of service, credit or benefit procured or obtained is $1,000 or more, guilty of a Class 5 felony or is, if the value is less than $1,000, guilty of a Class 1 misdemeanor.</p><p>Code 1950, § 18.1-120; 1960, c. 358; 1974, c. 615; 1975, cc. 14, 15; 1977, c. 178; 1981, c. 197; 1993, c. 575; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0764" target="_blank" rel="noreferrer">764</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0765" target="_blank" rel="noreferrer">765</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0089" target="_blank" rel="noreferrer">89</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',8),i=[n];function s(l,h,c,u,d,f){return o(),t("div",null,i)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
