import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-189 Defrauding keeper of motor vehicles or watercraft.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-189.md","filePath":"vacode/18.2-189.md"}'),o={name:"vacode/18.2-189.md"},i=t('<h1 id="§-18-2-189-defrauding-keeper-of-motor-vehicles-or-watercraft" tabindex="-1">§ 18.2-189 Defrauding keeper of motor vehicles or watercraft. <a class="header-anchor" href="#§-18-2-189-defrauding-keeper-of-motor-vehicles-or-watercraft" aria-label="Permalink to &quot;§ 18.2-189 Defrauding keeper of motor vehicles or watercraft.&quot;">​</a></h1><p>A person shall be guilty of a Class 2 misdemeanor if he:</p><ol><li><p>Stores a motor vehicle, boat or other watercraft with any person, firm or corporation engaged in the business of conducting a garage, marina, watercraft dealership or other facility for the (i) storage of motor vehicles, boats or other watercraft, (ii) furnishing of supplies to motor vehicles, boats or other watercraft, or (iii) alteration or repair of motor vehicles, boats or other watercraft, and obtains storage, supplies, alterations or repairs for such motor vehicle, boat or other watercraft, without having an express agreement for credit, or procures storage, supplies, alterations or repairs on account of such motor vehicle, boat or other watercraft so stored, without paying therefor, and with the intent to cheat or defraud the owner or keeper of the garage, marina or boat repair facility; or</p></li><li><p>With such intent, obtains credit at the garage, marina, watercraft dealership or boat repair facility for such storage, supplies, alterations or repairs through any misrepresentation or false statement; or</p></li><li><p>With such intent, removes or causes to be removed any such motor vehicle, boat or other watercraft from any such garage, marina, watercraft dealership or boat repair facility while there is a lien existing thereon for the proper charges due from him for storage, supplies, alterations or repairs furnished thereon, in accordance with the provisions of § <a href="http://law.lis.virginia.gov/vacode/43-32/" target="_blank" rel="noreferrer">43-32</a>, <a href="http://law.lis.virginia.gov/vacode/43-33/" target="_blank" rel="noreferrer">43-33</a>, <a href="http://law.lis.virginia.gov/vacode/46.2-644.01/" target="_blank" rel="noreferrer">46.2-644.01</a>, or § <a href="http://law.lis.virginia.gov/vacode/46.2-644.02/" target="_blank" rel="noreferrer">46.2-644.02</a>.</p></li></ol><p>Code 1950, § 18.1-121; 1960, c. 358; 1975, cc. 14, 15; 1978, c. 245; 1988, c. 414; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0664" target="_blank" rel="noreferrer">664</a>.</p>',4),s=[i];function n(c,h,l,f,p,d){return r(),a("div",null,s)}const m=e(o,[["render",n]]);export{u as __pageData,m as default};
