import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-19 How accessories after the fact punished; certain exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-19.md","filePath":"vacode/18.2-19.md"}'),o={name:"vacode/18.2-19.md"},s=r('<h1 id="§-18-2-19-how-accessories-after-the-fact-punished-certain-exceptions" tabindex="-1">§ 18.2-19 How accessories after the fact punished; certain exceptions. <a class="header-anchor" href="#§-18-2-19-how-accessories-after-the-fact-punished-certain-exceptions" aria-label="Permalink to &quot;§ 18.2-19 How accessories after the fact punished; certain exceptions.&quot;">​</a></h1><p>Every accessory after the fact is guilty of (i) a Class 6 felony in the case of a homicide offense that is punishable as a Class 1 or Class 2 felony or (ii) a Class 1 misdemeanor in the case of any other felony. However, no person in the relation of spouse, parent or grandparent, child or grandchild, or sibling, by consanguinity or affinity, or servant to the offender, who, after the commission of a felony, aids or assists a principal felon or accessory before the fact to avoid or escape from prosecution or punishment, shall be deemed an accessory after the fact.</p><p>Code 1950, §§ 18.1-11, 18.1-12; 1960, c. 358; 1975, cc. 14, 15; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0668" target="_blank" rel="noreferrer">668</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0900" target="_blank" rel="noreferrer">900</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>.</p>',3),i=[s];function n(c,f,l,h,p,d){return a(),t("div",null,i)}const u=e(o,[["render",n]]);export{g as __pageData,u as default};
