import{_ as e,o as a,c as o,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-196.1 Unlawful use of payment card scanning devices and re-encoders; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-196.1.md","filePath":"vacode/18.2-196.1.md"}'),r={name:"vacode/18.2-196.1.md"},t=n('<h1 id="§-18-2-196-1-unlawful-use-of-payment-card-scanning-devices-and-re-encoders-penalty" tabindex="-1">§ 18.2-196.1 Unlawful use of payment card scanning devices and re-encoders; penalty. <a class="header-anchor" href="#§-18-2-196-1-unlawful-use-of-payment-card-scanning-devices-and-re-encoders-penalty" aria-label="Permalink to &quot;§ 18.2-196.1 Unlawful use of payment card scanning devices and re-encoders; penalty.&quot;">​</a></h1><p>A. Any person who with malicious intent uses a scanning device or a re-encoder on the payment card of another without the permission of the authorized payment card user is guilty of a Class 1 misdemeanor.</p><p>B. Any person who violates this section and sells or distributes such information to another is guilty of a Class 6 felony.</p><p>C. Any person who violates this section and uses such information in the commission of another crime is guilty of a Class 6 felony.</p><p>D. For the purposes of this section:</p><ol><li><p>&quot;Authorized payment card user&quot; means any person with the authorization or permission to use any payment card to obtain, purchase, or receive goods, services, money, or anything else of value from a merchant.</p></li><li><p>&quot;Merchant&quot; means an owner or operator of any mercantile establishment or any agent, employee, lessee, consignee, officer, director, franchisee, or independent contractor of such owner or operator who receives from an authorized payment card user or someone he believes to be an authorized payment card user, a payment card or information from a payment card, or what he believes to be a payment card or information from a payment card, as the instrument for obtaining, purchasing or receiving goods, services, money, or anything else of value from him.</p></li><li><p>&quot;Payment card&quot; means a credit card, charge card, debit card, hotel key card, stored-value card, white plastic, or any other card containing encoded information that allows an authorized payment card user to obtain, purchase, or receive goods, services, money, or anything else of value from a merchant.</p></li><li><p>&quot;Re-encoder&quot; means an electronic device that transfers encoded information from the magnetic strip or stripe of a payment card onto the magnetic strip or stripe of a different payment card.</p></li><li><p>&quot;Scanning device&quot; means a scanner, reader, or any other electronic device that is used to access, read, scan, obtain, memorize, temporarily store, or permanently store encoded information on the magnetic strip or stripe of a payment card.</p></li></ol><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0166" target="_blank" rel="noreferrer">166</a>.</p>',7),i=[t];function s(c,d,p,l,m,h){return a(),o("div",null,i)}const y=e(r,[["render",s]]);export{f as __pageData,y as default};
