import{_ as e,o as i,c as o,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-204.2 Manufacture, sale, etc., or possession of fictitious, facsimile or simulated official license or identification; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-204.2.md","filePath":"vacode/18.2-204.2.md"}'),t={name:"vacode/18.2-204.2.md"},r=a('<h1 id="§-18-2-204-2-manufacture-sale-etc-or-possession-of-fictitious-facsimile-or-simulated-official-license-or-identification-penalty" tabindex="-1">§ 18.2-204.2 Manufacture, sale, etc., or possession of fictitious, facsimile or simulated official license or identification; penalty. <a class="header-anchor" href="#§-18-2-204-2-manufacture-sale-etc-or-possession-of-fictitious-facsimile-or-simulated-official-license-or-identification-penalty" aria-label="Permalink to &quot;§ 18.2-204.2 Manufacture, sale, etc., or possession of fictitious, facsimile or simulated official license or identification; penalty.&quot;">​</a></h1><p>A. Except as provided in subsection D of § <a href="http://law.lis.virginia.gov/vacode/18.2-204.1/" target="_blank" rel="noreferrer">18.2-204.1</a>, it shall be unlawful for any person to manufacture, advertise for sale, sell or possess any fictitious, facsimile or simulated driver&#39;s license issued by any state, territory or possession of the United States, the District of Columbia, the Commonwealth of Puerto Rico or any foreign country or government; United States Armed Forces identification card; United States passport or foreign government visa; Virginia Department of Motor Vehicles special identification card; official identification issued by any other federal, state or foreign government agency; or official student identification card of an institution of higher education, or in any way reproduce any identification card or facsimile thereof in such a manner that it could be mistaken for a valid license or identification of any type specified in this subsection.</p><p>B. Any person manufacturing, advertising for sale, selling or reproducing such card or facsimile thereof shall be guilty of a Class 1 misdemeanor.</p><p>C. Any person possessing any such card or facsimile thereof shall be guilty of a Class 2 misdemeanor.</p><p>D. The provisions of this section shall not preclude an election to prosecute under § <a href="http://law.lis.virginia.gov/vacode/18.2-172/" target="_blank" rel="noreferrer">18.2-172</a>, except to prosecute for forgery or uttering of such license or identification card or facsimile thereof as proof of age.</p><p>1980, c. 281; 1989, c. 705; 1992, c. 531; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0445" target="_blank" rel="noreferrer">445</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0484" target="_blank" rel="noreferrer">484</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',6),s=[r];function n(c,f,l,d,u,p){return i(),o("div",null,s)}const g=e(t,[["render",n]]);export{m as __pageData,g as default};
