import{_ as e,o as i,c as n,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-213.1 Obtaining certification as small, women-owned, or minority-owned business by deception; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-213.1.md","filePath":"vacode/18.2-213.1.md"}'),a={name:"vacode/18.2-213.1.md"},t=o('<h1 id="§-18-2-213-1-obtaining-certification-as-small-women-owned-or-minority-owned-business-by-deception-penalty" tabindex="-1">§ 18.2-213.1 Obtaining certification as small, women-owned, or minority-owned business by deception; penalty. <a class="header-anchor" href="#§-18-2-213-1-obtaining-certification-as-small-women-owned-or-minority-owned-business-by-deception-penalty" aria-label="Permalink to &quot;§ 18.2-213.1 Obtaining certification as small, women-owned, or minority-owned business by deception; penalty.&quot;">​</a></h1><p>A. Except as otherwise provided by § <a href="http://law.lis.virginia.gov/vacode/18.2-498.3/" target="_blank" rel="noreferrer">18.2-498.3</a>, a person shall be guilty of a Class 1 misdemeanor if, in the course of business, he:</p><ol><li><p>Fraudulently obtains or retains certification as a small, women-owned, or minority-owned business;</p></li><li><p>Willfully makes a false statement knowing it to be untrue, whether by affidavit, report or other representation, to an official or employee of a public body for the purpose of influencing the certification or denial of certification of any business entity as a small, women-owned, or minority-owned business;</p></li><li><p>Willfully obstructs or impedes any agency official or employee who is investigating the qualifications of a business entity which has requested certification as a small, women-owned, or minority-owned business; or</p></li><li><p>Fraudulently obtains public moneys reserved for or allocated or available to small, women-owned, or minority-owned businesses.</p></li></ol><p>B. For the purposes of this section, &quot;minority-owned business,&quot; and &quot;small business&quot; and &quot;women-owned business&quot; shall have the same meaning as those terms are defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-1604/" target="_blank" rel="noreferrer">2.2-1604</a>.</p><p>1987, c. 689; 1989, c. 570; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0831" target="_blank" rel="noreferrer">831</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0921" target="_blank" rel="noreferrer">921</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0482" target="_blank" rel="noreferrer">482</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0696" target="_blank" rel="noreferrer">696</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0697" target="_blank" rel="noreferrer">697</a>.</p>',5),r=[t];function s(l,c,f,p,d,b){return i(),n("div",null,r)}const g=e(a,[["render",s]]);export{m as __pageData,g as default};
