import{_ as e,o,c as t,O as n}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 18.2-22 Conspiracy to commit felony.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-22.md","filePath":"vacode/18.2-22.md"}'),a={name:"vacode/18.2-22.md"},i=n('<h1 id="§-18-2-22-conspiracy-to-commit-felony" tabindex="-1">§ 18.2-22 Conspiracy to commit felony. <a class="header-anchor" href="#§-18-2-22-conspiracy-to-commit-felony" aria-label="Permalink to &quot;§ 18.2-22 Conspiracy to commit felony.&quot;">​</a></h1><p>(a) If any person shall conspire, confederate or combine with another, either within or outside the Commonwealth, to commit a felony within the Commonwealth, or if he shall so conspire, confederate or combine with another within the Commonwealth to commit a felony either within or outside the Commonwealth, he shall be guilty of a felony that shall be punishable as follows:</p><p>(1) Every person who so conspires to commit an offense that is punishable as a Class 1 felony is guilty of a Class 3 felony;</p><p>(2) Every person who so conspires to commit an offense that is any other felony is guilty of a Class 5 felony; and</p><p>(3) Every person who so conspires to commit an offense the maximum punishment for which is confinement in a state correctional facility for a period of less than five years shall be confined in a state correctional facility for a period of one year, or, in the discretion of the jury or the court trying the case without a jury, may be confined in jail not exceeding 12 months and fined not exceeding $500, either or both.</p><p>(b) However, in no event shall the punishment for a conspiracy to commit an offense exceed the maximum punishment for the commission of the offense itself.</p><p>(c) Jurisdiction for the trial of any person accused of a conspiracy under this section shall be in the county or city wherein any part of such conspiracy is planned or in the county or city wherein any act is done toward the consummation of such plan or conspiracy.</p><p>(d) The penalty provisions of this section shall not apply to any person who conspires to commit any offense defined in the Drug Control Act (§ <a href="/vacode/54.1-3400/">54.1-3400</a> et seq.) or of Article 1 (§ <a href="/vacode/18.2-247/">18.2-247</a> et seq.) of Chapter 7. The penalty for any such violation shall be as provided in § <a href="/vacode/18.2-256/">18.2-256</a>.</p><p>Code 1950, § 18.1-15.3; 1972, c. 484; 1973, c. 399; 1975, cc. 14, 15; 1983, c. 19; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>.</p>',9),r=[i];function s(c,h,l,f,p,m){return o(),t("div",null,r)}const u=e(a,[["render",s]]);export{d as __pageData,u as default};
