import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-246.10 Shipping requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-246.10.md","filePath":"vacode/18.2-246.10.md"}'),t={name:"vacode/18.2-246.10.md"},o=r('<h1 id="§-18-2-246-10-shipping-requirements" tabindex="-1">§ 18.2-246.10 Shipping requirements. <a class="header-anchor" href="#§-18-2-246-10-shipping-requirements" aria-label="Permalink to &quot;§ 18.2-246.10 Shipping requirements.&quot;">​</a></h1><p>Each person who mails, ships, or otherwise delivers cigarettes in connection with a delivery sale:</p><ol><li><p>Shall include as part of the shipping documents a clear and conspicuous statement providing as follows: &quot;Cigarettes: Virginia Law Prohibits Shipping to Individuals Under 21, and Requires the Payment of all Applicable Taxes&quot;;</p></li><li><p>Shall use a method of mailing, shipping, or delivery that obligates the delivery service or any party making delivery to require (i) the consumer placing the purchase order for the delivery sale, or an adult of legal minimum purchase age, to sign to accept delivery of the shipping container, and (ii) proof, in the form of a valid, government-issued identification bearing a photograph of the individual who signs to accept delivery of the shipping container, demonstrating that he is either the addressee who is of legal minimum purchase age or another adult of legal minimum purchase age. However, proof of the legal minimum purchase age shall be required only if such individual appears to be under 27 years of age; and</p></li><li><p>Shall provide to the delivery service retained for such delivery sale evidence of full compliance with § <a href="/vacode/18.2-246.12/">18.2-246.12</a>.</p></li></ol><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1010" target="_blank" rel="noreferrer">1010</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0090" target="_blank" rel="noreferrer">90</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0102" target="_blank" rel="noreferrer">102</a>.</p>',4),n=[o];function l(s,p,h,c,d,g){return i(),a("div",null,n)}const m=e(t,[["render",l]]);export{f as __pageData,m as default};
