import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-246.11 Registration and reporting requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-246.11.md","filePath":"vacode/18.2-246.11.md"}'),i={name:"vacode/18.2-246.11.md"},n=r('<h1 id="§-18-2-246-11-registration-and-reporting-requirements" tabindex="-1">§ 18.2-246.11 Registration and reporting requirements. <a class="header-anchor" href="#§-18-2-246-11-registration-and-reporting-requirements" aria-label="Permalink to &quot;§ 18.2-246.11 Registration and reporting requirements.&quot;">​</a></h1><p>A. Prior to making delivery sales or mailing, shipping, or otherwise delivering cigarettes in connection with any such delivery sales, every person shall file with the Board and with the Attorney General a statement setting forth such person&#39;s name, trade name, and the address of such person&#39;s principal place of business and any other place of business.</p><p>B. Not later than the tenth day of each calendar month, each person that has made a delivery sale or mailed, shipped, or otherwise delivered cigarettes in connection with any such delivery sale during the previous calendar month shall file with the Board and with the Attorney General a report in the format prescribed by the Board, which may include an electronic format, that provides for each and every such delivery sale:</p><ol><li><p>The name and address of the consumer to whom such delivery sale was made;</p></li><li><p>The brand or brands of the cigarettes that were sold in such delivery sale; and</p></li><li><p>The quantity of cigarettes that were sold in such delivery sale.</p></li></ol><p>C. Any person who satisfies the requirements of § 376 of Title 15 of the United States Code shall be deemed to satisfy the requirements of this section.</p><p>D. For purposes of any penalty that may be imposed for a violation of this section, a failure to file a particular statement or report with both the Board and the Attorney General shall constitute a single violation.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1010" target="_blank" rel="noreferrer">1010</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0847" target="_blank" rel="noreferrer">847</a>.</p>',7),o=[n];function s(l,h,d,c,p,f){return t(),a("div",null,o)}const g=e(i,[["render",s]]);export{u as __pageData,g as default};
