import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-246.13 Civil penalties; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-246.13.md","filePath":"vacode/18.2-246.13.md"}'),r={name:"vacode/18.2-246.13.md"},o=t('<h1 id="§-18-2-246-13-civil-penalties-penalties" tabindex="-1">§ 18.2-246.13 Civil penalties; penalties. <a class="header-anchor" href="#§-18-2-246-13-civil-penalties-penalties" aria-label="Permalink to &quot;§ 18.2-246.13 Civil penalties; penalties.&quot;">​</a></h1><p>A. In addition to any criminal penalties for violations of this article and except for civil penalties otherwise provided in this article, a first violation of any provision of this article shall be punishable by a civil penalty of no more than $1,000. A second or subsequent violation of any provision of this article shall be punishable by a civil penalty of no more than $10,000.</p><p>B. Any prospective consumer who knowingly submits a false certification under subdivision A 1 of § <a href="http://law.lis.virginia.gov/vacode/18.2-246.8/" target="_blank" rel="noreferrer">18.2-246.8</a> shall be subject to a civil penalty of no more than $5,000 for each such offense.</p><p>C. Any person failing to collect or remit to the Board or the Department of Taxation any tax required in connection with a delivery sale shall be assessed, in addition to any other applicable penalty, a civil penalty of no more than five times the retail value of the cigarettes involved.</p><p>D. Any civil penalty collected under this article shall be paid to the general fund.</p><p>E. Any person who fails to file the statement required by subsection A of § <a href="http://law.lis.virginia.gov/vacode/18.2-246.11/" target="_blank" rel="noreferrer">18.2-246.11</a> and thereafter makes a delivery sale is guilty of a Class 1 misdemeanor and for any second or subsequent offense is guilty of a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-498.3/" target="_blank" rel="noreferrer">18.2-498.3</a>.</p><p>F. Any person who knowingly and with the intent to defraud, mislead, or deceive makes a statement filed as required by subsection A of § <a href="http://law.lis.virginia.gov/vacode/18.2-246.11/" target="_blank" rel="noreferrer">18.2-246.11</a> which is false is guilty of a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-498.3/" target="_blank" rel="noreferrer">18.2-498.3</a>. Each such filed statement containing one or more false statements shall constitute a separate offense.</p><p>G. Any person who fails to make the report required by subsection B of § <a href="http://law.lis.virginia.gov/vacode/18.2-246.11/" target="_blank" rel="noreferrer">18.2-246.11</a> is guilty of a Class 1 misdemeanor and for any second or subsequent offense is guilty of a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-498.3/" target="_blank" rel="noreferrer">18.2-498.3</a>.</p><p>H. Any person who knowingly and with the intent to defraud, mislead, or deceive makes a materially false statement in any report required by subsection B of § <a href="http://law.lis.virginia.gov/vacode/18.2-246.11/" target="_blank" rel="noreferrer">18.2-246.11</a> is guilty of a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-498.3/" target="_blank" rel="noreferrer">18.2-498.3</a>. Each such report containing one or more false statements constitutes a separate offense.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1010" target="_blank" rel="noreferrer">1010</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0847" target="_blank" rel="noreferrer">847</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0625" target="_blank" rel="noreferrer">625</a>.</p>',10),n=[o];function l(s,f,c,p,h,d){return a(),i("div",null,n)}const u=e(r,[["render",l]]);export{g as __pageData,u as default};
