import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-246.15 Enforcement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-246.15.md","filePath":"vacode/18.2-246.15.md"}'),n={name:"vacode/18.2-246.15.md"},o=a('<h1 id="§-18-2-246-15-enforcement" tabindex="-1">§ 18.2-246.15 Enforcement. <a class="header-anchor" href="#§-18-2-246-15-enforcement" aria-label="Permalink to &quot;§ 18.2-246.15 Enforcement.&quot;">​</a></h1><p>The Attorney General is authorized to enforce the provisions of this article. The Attorney General may assess the civil penalties authorized by this article, with the concurrence of the attorney for the Commonwealth pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-511/" target="_blank" rel="noreferrer">2.2-511</a>, may prosecute criminal violations under this article, and may bring an action in the appropriate court to collect assessed penalties or prevent or restrain violations of this article by any person, or any person controlling such person. The Board and the State Department of Taxation shall cooperate with the Attorney General in his enforcement efforts and provide to the Attorney General all information and documentation in their possession necessary for the Attorney General to accomplish such enforcement.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1010" target="_blank" rel="noreferrer">1010</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0847" target="_blank" rel="noreferrer">847</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0625" target="_blank" rel="noreferrer">625</a>.</p>',3),i=[o];function s(c,l,h,p,f,_){return t(),r("div",null,i)}const g=e(n,[["render",s]]);export{m as __pageData,g as default};
