import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-246.6 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-246.6.md","filePath":"vacode/18.2-246.6.md"}'),r={name:"vacode/18.2-246.6.md"},i=o('<h1 id="§-18-2-246-6-definitions" tabindex="-1">§ 18.2-246.6 Definitions. <a class="header-anchor" href="#§-18-2-246-6-definitions" aria-label="Permalink to &quot;§ 18.2-246.6 Definitions.&quot;">​</a></h1><p>For purposes of this article:</p><p>&quot;Adult&quot; means a person who is at least the legal minimum purchasing age.</p><p>&quot;Board&quot; means the Board of Directors of the Virginia Alcoholic Beverage Control Authority.</p><p>&quot;Consumer&quot; means an individual who is not permitted as a wholesaler pursuant to § <a href="/vacode/58.1-1011/">58.1-1011</a> or who is not a retailer.</p><p>&quot;Delivery sale&quot; means any sale of cigarettes to a consumer in the Commonwealth regardless of whether the seller is located in the Commonwealth where either (i) the purchaser submits the order for such sale by means of a telephonic or other method of voice transmission, the mails or any other delivery service, or the Internet or other online service; or (ii) the cigarettes are delivered by use of the mails or a delivery service. A sale of cigarettes not for personal consumption to a person who is a wholesale dealer or retail dealer, as such terms are defined in § <a href="/vacode/58.1-1000/">58.1-1000</a>, shall not be a delivery sale. A delivery of cigarettes, not through the mail or by a common carrier, to a consumer performed by the owner, employee or other individual acting on behalf of a retailer authorized to sell such cigarettes shall not be a delivery sale.</p><p>&quot;Delivery service&quot; means any person who is engaged in the commercial delivery of letters, packages, or other containers.</p><p>&quot;Legal minimum purchasing age&quot; is the minimum age at which an individual may legally purchase cigarettes in the Commonwealth.</p><p>&quot;Mails&quot; or &quot;mailing&quot; means the shipment of cigarettes through the United States Postal Service.</p><p>&quot;Shipping container&quot; means a container in which cigarettes are shipped in connection with a delivery sale.</p><p>&quot;Shipping documents&quot; means bills of lading, airbills, or any other documents used to evidence the undertaking by a delivery service to deliver letters, packages, or other containers.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1010" target="_blank" rel="noreferrer">1010</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0839" target="_blank" rel="noreferrer">839</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0038" target="_blank" rel="noreferrer">38</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0730" target="_blank" rel="noreferrer">730</a>.</p>',12),n=[i];function s(l,h,c,p,d,u){return t(),a("div",null,n)}const f=e(r,[["render",s]]);export{g as __pageData,f as default};
