import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-246.9 Disclosure requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-246.9.md","filePath":"vacode/18.2-246.9.md"}'),i={name:"vacode/18.2-246.9.md"},o=r('<h1 id="§-18-2-246-9-disclosure-requirements" tabindex="-1">§ 18.2-246.9 Disclosure requirements. <a class="header-anchor" href="#§-18-2-246-9-disclosure-requirements" aria-label="Permalink to &quot;§ 18.2-246.9 Disclosure requirements.&quot;">​</a></h1><p>The notice required under subdivision A 3 of § <a href="http://law.lis.virginia.gov/vacode/18.2-246.8/" target="_blank" rel="noreferrer">18.2-246.8</a> shall include:</p><ol><li><p>A prominent and clearly legible statement that cigarette sales to consumers below the legal minimum purchase age are illegal;</p></li><li><p>A prominent and clearly legible statement that consists of one of the warnings set forth in section 4(a)(1) of the Federal Cigarette Labeling and Advertising Act (15 U.S.C. § 1333 (a)(1)) rotated on a quarterly basis;</p></li><li><p>A prominent and clearly legible statement that sales of cigarettes are restricted to those consumers who provide verifiable proof of age in accordance with § <a href="http://law.lis.virginia.gov/vacode/18.2-246.8/" target="_blank" rel="noreferrer">18.2-246.8</a>; and</p></li><li><p>A prominent and clearly legible statement that cigarette sales are subject to tax under § <a href="http://law.lis.virginia.gov/vacode/58.1-1001/" target="_blank" rel="noreferrer">58.1-1001</a>, and an explanation of how such tax has been, or is to be, paid with respect to such delivery sale.</p></li></ol><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1010" target="_blank" rel="noreferrer">1010</a>.</p>',4),n=[o];function l(s,c,d,h,p,_){return a(),t("div",null,n)}const f=e(i,[["render",l]]);export{g as __pageData,f as default};
