import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-248.02 Allowing a minor or incapacitated person to be present during manufacture or attempted manufacture of methamphetamine prohibited; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-248.02.md","filePath":"vacode/18.2-248.02.md"}'),r={name:"vacode/18.2-248.02.md"},i=n('<h1 id="§-18-2-248-02-allowing-a-minor-or-incapacitated-person-to-be-present-during-manufacture-or-attempted-manufacture-of-methamphetamine-prohibited-penalties" tabindex="-1">§ 18.2-248.02 Allowing a minor or incapacitated person to be present during manufacture or attempted manufacture of methamphetamine prohibited; penalties. <a class="header-anchor" href="#§-18-2-248-02-allowing-a-minor-or-incapacitated-person-to-be-present-during-manufacture-or-attempted-manufacture-of-methamphetamine-prohibited-penalties" aria-label="Permalink to &quot;§ 18.2-248.02 Allowing a minor or incapacitated person to be present during manufacture or attempted manufacture of methamphetamine prohibited; penalties.&quot;">​</a></h1><p>Any person 18 years of age or older who knowingly allows (i) a minor under the age of 15, (ii) a minor 15 years of age or older with whom he maintains a custodial relationship, including but not limited to as a parent, step-parent, grandparent, step-grandparent, or who stands in loco parentis with respect to such minor, or (iii) a mentally incapacitated or physically helpless person of any age, to be present in the same dwelling, apartment as defined by § <a href="/vacode/55.1-2000/">55.1-2000</a>, unit of a hotel as defined in § <a href="/vacode/35.1-1/">35.1-1</a>, garage, shed, or vehicle during the manufacture or attempted manufacture of methamphetamine as prohibited by subsection C1 of § <a href="/vacode/18.2-248/">18.2-248</a> is guilty of a felony punishable by imprisonment for not less than 10 nor more than 40 years. This penalty shall be in addition to and served consecutively with any other sentence.</p><p>2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0923" target="_blank" rel="noreferrer">923</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0941" target="_blank" rel="noreferrer">941</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0743" target="_blank" rel="noreferrer">743</a>.</p>',3),o=[i];function s(p,l,c,d,m,h){return a(),t("div",null,o)}const g=e(r,[["render",s]]);export{u as __pageData,g as default};
