import{_ as e,o as r,c as t,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-251.02 Drug Offender Assessment and Treatment Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-251.02.md","filePath":"vacode/18.2-251.02.md"}'),a={name:"vacode/18.2-251.02.md"},s=n('<h1 id="§-18-2-251-02-drug-offender-assessment-and-treatment-fund" tabindex="-1">§ 18.2-251.02 Drug Offender Assessment and Treatment Fund. <a class="header-anchor" href="#§-18-2-251-02-drug-offender-assessment-and-treatment-fund" aria-label="Permalink to &quot;§ 18.2-251.02 Drug Offender Assessment and Treatment Fund.&quot;">​</a></h1><p>There is hereby established in the state treasury the Drug Offender Assessment and Treatment Fund, which shall consist of moneys received from fees imposed on certain drug offense convictions pursuant to § <a href="/vacode/16.1-69.48:3/">16.1-69.48:3</a> and subdivisions A 10 and 11 of § <a href="/vacode/17.1-275/">17.1-275</a>. All interest derived from the deposit and investment of moneys in the Fund shall be credited to the Fund. Any moneys not appropriated by the General Assembly shall remain in the Drug Offender Assessment and Treatment Fund and shall not be transferred or revert to the general fund at the end of any fiscal year. All moneys in the Fund shall be subject to annual appropriation by the General Assembly to the Department of Corrections, the Department of Juvenile Justice, and the Commission on VASAP to implement and operate the offender substance abuse screening and assessment program; the Department of Criminal Justice Services for the support of community-based probation and local pretrial services agencies; and the Office of the Executive Secretary of the Supreme Court of Virginia for the support of drug treatment court programs.</p><p>1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0606" target="_blank" rel="noreferrer">606</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1004" target="_blank" rel="noreferrer">1004</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1285" target="_blank" rel="noreferrer">1285</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1286" target="_blank" rel="noreferrer">1286</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',3),i=[s];function o(l,f,d,c,h,g){return r(),t("div",null,i)}const m=e(a,[["render",o]]);export{u as __pageData,m as default};
