import{_ as e,o,c as r,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-251.03 Arrest and prosecution when experiencing or reporting overdoses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-251.03.md","filePath":"vacode/18.2-251.03.md"}'),i={name:"vacode/18.2-251.03.md"},a=n('<h1 id="§-18-2-251-03-arrest-and-prosecution-when-experiencing-or-reporting-overdoses" tabindex="-1">§ 18.2-251.03 Arrest and prosecution when experiencing or reporting overdoses. <a class="header-anchor" href="#§-18-2-251-03-arrest-and-prosecution-when-experiencing-or-reporting-overdoses" aria-label="Permalink to &quot;§ 18.2-251.03 Arrest and prosecution when experiencing or reporting overdoses.&quot;">​</a></h1><p>A. For purposes of this section, &quot;overdose&quot; means a life-threatening condition resulting from the consumption or use of a controlled substance, alcohol, or any combination of such substances.</p><p>B. No individual shall be subject to arrest or prosecution for the unlawful purchase, possession, or consumption of alcohol pursuant to § <a href="/vacode/4.1-305/">4.1-305</a>, unlawful purchase, possession, or consumption of marijuana pursuant to § <a href="/vacode/4.1-1105.1/">4.1-1105.1</a>, possession of a controlled substance pursuant to § <a href="/vacode/18.2-250/">18.2-250</a>, intoxication in public pursuant to § <a href="/vacode/18.2-388/">18.2-388</a>, or possession of controlled paraphernalia pursuant to § <a href="/vacode/54.1-3466/">54.1-3466</a> if:</p><ol><li><p>Such individual (i) in good faith, seeks or obtains emergency medical attention (a) for himself, if he is experiencing an overdose, or (b) for another individual, if such other individual is experiencing an overdose; (ii) is experiencing an overdose and another individual, in good faith, seeks or obtains emergency medical attention for such individual, by contemporaneously reporting such overdose to a firefighter, as defined in § <a href="/vacode/65.2-102/">65.2-102</a>, emergency medical services personnel, as defined in § <a href="/vacode/32.1-111.1/">32.1-111.1</a>, a law-enforcement officer, as defined in § <a href="/vacode/9.1-101/">9.1-101</a>, or an emergency 911 system; or (iii) in good faith, renders emergency care or assistance, including cardiopulmonary resuscitation (CPR) or the administration of naloxone or other opioid antagonist for overdose reversal, to an individual experiencing an overdose while another individual seeks or obtains emergency medical attention in accordance with this subdivision;</p></li><li><p>Such individual remains at the scene of the overdose or at any alternative location to which he or the person requiring emergency medical attention has been transported until a law-enforcement officer responds to the report of an overdose. If no law-enforcement officer is present at the scene of the overdose or at the alternative location, then such individual shall cooperate with law enforcement as otherwise set forth herein;</p></li><li><p>Such individual identifies himself to the law-enforcement officer who responds to the report of the overdose; and</p></li><li><p>The evidence for the prosecution of an offense enumerated in this subsection was obtained as a result of the individual seeking or obtaining emergency medical attention or rendering emergency care or assistance.</p></li></ol><p>C. The provisions of this section shall not apply to any person who seeks or obtains emergency medical attention for himself or another individual, to a person experiencing an overdose when another individual seeks or obtains emergency medical attention for him, or to a person who renders emergency care or assistance to an individual experiencing an overdose while another person seeks or obtains emergency medical attention during the execution of a search warrant or during the conduct of a lawful search or a lawful arrest.</p><p>D. This section does not establish protection from arrest or prosecution for any individual or offense other than those listed in subsection B.</p><p>E. No law-enforcement officer acting in good faith shall be found liable for false arrest if it is later determined that the person arrested was immune from prosecution under this section.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0418" target="_blank" rel="noreferrer">418</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0436" target="_blank" rel="noreferrer">436</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0626" target="_blank" rel="noreferrer">626</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1016" target="_blank" rel="noreferrer">1016</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0029" target="_blank" rel="noreferrer">29</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',8),t=[a];function s(c,l,d,h,f,p){return o(),r("div",null,t)}const v=e(i,[["render",s]]);export{g as __pageData,v as default};
