import{_ as e,o as r,c as o,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-251.1:3 Possession or distribution of cannabis oil, or industrial hemp; laboratories; Department of Agriculture and Consumer Services employees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-251.1:3.md","filePath":"vacode/18.2-251.1:3.md"}'),i={name:"vacode/18.2-251.1:3.md"},s=a('<h1 id="§-18-2-251-1-3-possession-or-distribution-of-cannabis-oil-or-industrial-hemp-laboratories-department-of-agriculture-and-consumer-services-employees" tabindex="-1">§ 18.2-251.1:3 Possession or distribution of cannabis oil, or industrial hemp; laboratories; Department of Agriculture and Consumer Services employees. <a class="header-anchor" href="#§-18-2-251-1-3-possession-or-distribution-of-cannabis-oil-or-industrial-hemp-laboratories-department-of-agriculture-and-consumer-services-employees" aria-label="Permalink to &quot;§ 18.2-251.1:3 Possession or distribution of cannabis oil, or industrial hemp; laboratories; Department of Agriculture and Consumer Services employees.&quot;">​</a></h1><p>A. No person employed by an analytical laboratory to retrieve, deliver, or possess cannabis oil or industrial hemp samples from a permitted pharmaceutical processor, a registered industrial hemp grower, a federally licensed hemp producer, or a registered industrial hemp processor for the purpose of performing required testing shall be prosecuted under Chapter 11 (§ <a href="/vacode/4.1-1100/">4.1-1100</a> et seq.) of Title 4.1 or § <a href="/vacode/18.2-248/">18.2-248</a>, <a href="/vacode/18.2-248.1/">18.2-248.1</a>, <a href="/vacode/18.2-250/">18.2-250</a>, or <a href="/vacode/18.2-255/">18.2-255</a> for the possession or distribution of cannabis oil or industrial hemp or for storing cannabis oil or industrial hemp for testing purposes in accordance with regulations promulgated by the Board of Pharmacy and the Board of Agriculture and Consumer Services.</p><p>B. No employee of the Department of Agriculture and Consumer Services shall be prosecuted under § <a href="/vacode/18.2-247/">18.2-247</a>, <a href="/vacode/18.2-248/">18.2-248</a>, <a href="/vacode/18.2-248.01/">18.2-248.01</a>, <a href="/vacode/18.2-248.1/">18.2-248.1</a>, or <a href="/vacode/18.2-250/">18.2-250</a> for the possession or distribution of industrial hemp when possession of industrial hemp is necessary in the performance of his duties.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0941" target="_blank" rel="noreferrer">941</a>, § 18.2-251.1:2; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0110" target="_blank" rel="noreferrer">110</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',4),t=[s];function n(l,c,d,p,u,f){return r(),o("div",null,t)}const g=e(i,[["render",n]]);export{m as __pageData,g as default};
