import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 18.2-258.02 Maintaining a fortified drug house; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-258.02.md","filePath":"vacode/18.2-258.02.md"}'),i={name:"vacode/18.2-258.02.md"},n=t('<h1 id="§-18-2-258-02-maintaining-a-fortified-drug-house-penalty" tabindex="-1">§ 18.2-258.02 Maintaining a fortified drug house; penalty. <a class="header-anchor" href="#§-18-2-258-02-maintaining-a-fortified-drug-house-penalty" aria-label="Permalink to &quot;§ 18.2-258.02 Maintaining a fortified drug house; penalty.&quot;">​</a></h1><p>Any office, store, shop, restaurant, dance hall, theater, poolroom, clubhouse, storehouse, warehouse, dwelling house, apartment or building or structure of any kind which is (i) substantially altered from its original status by means of reinforcement with the intent to impede, deter or delay lawful entry by a law-enforcement officer into such structure, (ii) being used for the purpose of manufacturing or distributing controlled substances or marijuana, and (iii) the object of a valid search warrant, shall be considered a fortified drug house. Any person who maintains or operates a fortified drug house is guilty of a Class 5 felony.</p><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0913" target="_blank" rel="noreferrer">913</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>.</p>',3),o=[n];function s(l,f,c,u,d,g){return a(),r("div",null,o)}const _=e(i,[["render",s]]);expo