import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 18.2-258 Certain premises deemed common nuisance; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-258.md","filePath":"vacode/18.2-258.md"}'),o={name:"vacode/18.2-258.md"},t=n('<h1 id="§-18-2-258-certain-premises-deemed-common-nuisance-penalty" tabindex="-1">§ 18.2-258 Certain premises deemed common nuisance; penalty. <a class="header-anchor" href="#§-18-2-258-certain-premises-deemed-common-nuisance-penalty" aria-label="Permalink to &quot;§ 18.2-258 Certain premises deemed common nuisance; penalty.&quot;">​</a></h1><p>Any office, store, shop, restaurant, dance hall, theater, poolroom, clubhouse, storehouse, warehouse, dwelling house, apartment, building of any kind, vehicle, vessel, boat, or aircraft, which with the knowledge of the owner, lessor, agent of any such lessor, manager, chief executive officer, operator, or tenant thereof, is frequented by persons under the influence of illegally obtained controlled substances or marijuana, as defined in § <a href="http://law.lis.virginia.gov/vacode/54.1-3401/" target="_blank" rel="noreferrer">54.1-3401</a>, or for the purpose of illegally obtaining possession of, manufacturing or distributing controlled substances or marijuana, or is used for the illegal possession, manufacture or distribution of controlled substances or marijuana shall be deemed a common nuisance. Any such owner, lessor, agent of any such lessor, manager, chief executive officer, operator, or tenant who knowingly permits, establishes, keeps or maintains such a common nuisance is guilty of a Class 1 misdemeanor and, for a second or subsequent offense, a Class 6 felony.</p><p>Code 1950, § 54-524.104:2; 1972, c. 736; 1973, c. 400; 1975, cc. 14, 15; 1979, c. 435; 1990, c. 948; 1992, cc. 248, 538; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0462" target="_blank" rel="noreferrer">462</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>.</p>',3),i=[t];function s(l,c,f,d,u,h){return a(),r("div",null,i)}const g=e(o,[["render",s]]);export{p as __pageData,g as default};
