import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-265.11 Exemption from participation in electronic system and maintenance of a written log.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-265.11.md","filePath":"vacode/18.2-265.11.md"}'),n={name:"vacode/18.2-265.11.md"},o=a('<h1 id="§-18-2-265-11-exemption-from-participation-in-electronic-system-and-maintenance-of-a-written-log" tabindex="-1">§ 18.2-265.11 Exemption from participation in electronic system and maintenance of a written log. <a class="header-anchor" href="#§-18-2-265-11-exemption-from-participation-in-electronic-system-and-maintenance-of-a-written-log" aria-label="Permalink to &quot;§ 18.2-265.11 Exemption from participation in electronic system and maintenance of a written log.&quot;">​</a></h1><p>A. The following entities shall not be required to participate in the electronic system and shall not be required to maintain a written log:</p><ol><li><p>Licensed manufacturers that manufacture and lawfully distribute products in the channels of commerce.</p></li><li><p>Wholesalers that lawfully distribute products in the channels of commerce.</p></li><li><p>Inpatient pharmacies of health care facilities licensed in the Commonwealth.</p></li><li><p>Licensed long-term health care facilities.</p></li><li><p>Government-operated health care clinics or departments or centers.</p></li><li><p>Physicians who dispense drugs pursuant to § <a href="http://law.lis.virginia.gov/vacode/54.1-3304/" target="_blank" rel="noreferrer">54.1-3304</a>.</p></li><li><p>Pharmacies located in correctional facilities.</p></li><li><p>Government-operated or industry-operated medical facilities serving the employees of the Commonwealth or local or federal government.</p></li></ol><p>B. Purchases of ephedrine or related compounds pursuant to a valid prescription are not required to be reported to the system or entered into a written log.</p><p>C. The sale of a single package containing no more than 60 milligrams of ephedrine or related compounds to an individual is not required to be reported to the system or entered into a log provided it is an isolated sale.</p><p>2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0160" target="_blank" rel="noreferrer">160</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0252" target="_blank" rel="noreferrer">252</a>.</p>',6),r=[o];function l(c,s,p,d,m,h){return t(),i("div",null,r)}const _=e(n,[["render",l]]);export{u as __pageData,_ as default};
