import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-265.20 Sale or distribution of dextromethorphan to minors; purchase by minors; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-265.20.md","filePath":"vacode/18.2-265.20.md"}'),a={name:"vacode/18.2-265.20.md"},i=r('<h1 id="§-18-2-265-20-sale-or-distribution-of-dextromethorphan-to-minors-purchase-by-minors-civil-penalty" tabindex="-1">§ 18.2-265.20 Sale or distribution of dextromethorphan to minors; purchase by minors; civil penalty. <a class="header-anchor" href="#§-18-2-265-20-sale-or-distribution-of-dextromethorphan-to-minors-purchase-by-minors-civil-penalty" aria-label="Permalink to &quot;§ 18.2-265.20 Sale or distribution of dextromethorphan to minors; purchase by minors; civil penalty.&quot;">​</a></h1><p>A. It is unlawful for any pharmacy or retail distributor knowingly or intentionally to sell or distribute any product containing dextromethorphan to a minor.</p><p>B. A pharmacy or retail distributor, or its employee or agent, shall not sell or distribute a product containing dextromethorphan unless the purchaser presents a federal, state, or local government-issued document that contains a photograph and the birth date of the purchaser that shows that the purchaser is at least 18 years of age or unless from the purchaser&#39;s outward appearance the pharmacy or retail distributor would reasonably presume the purchaser to be 25 years of age or older.</p><p>C. It is unlawful for any minor knowingly or intentionally to purchase any product containing dextromethorphan.</p><p>D. Any pharmacy or retail distributor, or its employee or agent, that violates subsection A or any minor who violates subsection C is subject to a civil penalty of $25. Any pharmacy or retail distributor, or its employee or agent, that violates subsection B shall receive a notice of noncompliance and, upon any subsequent violation of subsection B, shall be subject to a civil penalty of $25. Such penalty shall be collected by the attorney for the Commonwealth for the locality where the violation occurred, and the proceeds shall be deposited into the Literary Fund.</p><p>E. The provisions of this section shall not apply if the product was obtained directly from, or pursuant to, a valid prescription or order of a practitioner while acting in the course of his professional practice, or except as otherwise authorized by the Drug Control Act (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3400/" target="_blank" rel="noreferrer">54.1-3400</a> et seq.).</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0101" target="_blank" rel="noreferrer">101</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0362" target="_blank" rel="noreferrer">362</a>.</p>',7),n=[i];function s(l,c,h,p,d,u){return t(),o("div",null,n)}const y=e(a,[["render",s]]);export{f as __pageData,y as default};
