import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 18.2-267 Preliminary analysis of breath to determine alcoholic content of blood.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-267.md","filePath":"vacode/18.2-267.md"}'),r={name:"vacode/18.2-267.md"},i=a('<h1 id="§-18-2-267-preliminary-analysis-of-breath-to-determine-alcoholic-content-of-blood" tabindex="-1">§ 18.2-267 Preliminary analysis of breath to determine alcoholic content of blood. <a class="header-anchor" href="#§-18-2-267-preliminary-analysis-of-breath-to-determine-alcoholic-content-of-blood" aria-label="Permalink to &quot;§ 18.2-267 Preliminary analysis of breath to determine alcoholic content of blood.&quot;">​</a></h1><p>A. Any person who is suspected of a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-266/" target="_blank" rel="noreferrer">18.2-266</a>, <a href="http://law.lis.virginia.gov/vacode/18.2-266.1/" target="_blank" rel="noreferrer">18.2-266.1</a>, subsection B of § <a href="http://law.lis.virginia.gov/vacode/18.2-272/" target="_blank" rel="noreferrer">18.2-272</a>, or a similar ordinance shall be entitled, if such equipment is available, to have his breath analyzed to determine the probable alcoholic content of his blood. The person shall also be entitled, upon request, to observe the process of analysis and to see the blood-alcohol reading on the equipment used to perform the breath test. His breath may be analyzed by any police officer of the Commonwealth, or of any county, city or town, or by any member of a sheriff&#39;s department in the normal discharge of his duties.</p><p>B. The Department of Forensic Science shall determine the proper method and equipment to be used in analyzing breath samples taken pursuant to this section and shall advise the respective police and sheriff&#39;s departments of the same.</p><p>C. Any person who has been stopped by a police officer of the Commonwealth, or of any county, city or town, or by any member of a sheriff&#39;s department and is suspected by such officer to be guilty of an offense listed in subsection A, shall have the right to refuse to permit his breath to be so analyzed, and his failure to permit such analysis shall not be evidence in any prosecution for an offense listed in subsection A.</p><p>D. Whenever the breath sample analysis indicates that alcohol is present in the person&#39;s blood, the officer may charge the person with a violation of an offense listed in subsection A. The person so charged shall then be subject to the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/18.2-268.1/" target="_blank" rel="noreferrer">18.2-268.1</a> through <a href="http://law.lis.virginia.gov/vacode/18.2-268.12/" target="_blank" rel="noreferrer">18.2-268.12</a>, or of a similar ordinance.</p><p>E. The results of the breath analysis shall not be admitted into evidence in any prosecution for an offense listed in subsection A, the purpose of this section being to permit a preliminary analysis of the alcoholic content of the blood of a person suspected of having committed an offense listed in subsection A.</p><p>F. Police officers or members of any sheriff&#39;s department shall, upon stopping any person suspected of having committed an offense listed in subsection A, advise the person of his rights under the provisions of this section.</p><p>G. Nothing in this section shall be construed as limiting the provisions of §§ <a href="http://law.lis.virginia.gov/vacode/18.2-268.1/" target="_blank" rel="noreferrer">18.2-268.1</a> through <a href="http://law.lis.virginia.gov/vacode/18.2-268.12/" target="_blank" rel="noreferrer">18.2-268.12</a>.</p><p>Code 1950, § 18.1-54.1; 1970, c. 511; 1975, cc. 14, 15; 1979, c. 717; 1985, cc. 355, 609; 1990, c. 825; 1992, c. 830; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0359" target="_blank" rel="noreferrer">359</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0363" target="_blank" rel="noreferrer">363</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0154" target="_blank" rel="noreferrer">154</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0952" target="_blank" rel="noreferrer">952</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1013" target="_blank" rel="noreferrer">1013</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0757" target="_blank" rel="noreferrer">757</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>.</p>',9),n=[i];function s(l,h,c,f,p,d){return o(),t("div",null,n)}const v=e(r,[["render",s]]);export{b as __pageData,v as default};
