import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-268.11 Substantial compliance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-268.11.md","filePath":"vacode/18.2-268.11.md"}'),i={name:"vacode/18.2-268.11.md"},r=n('<h1 id="§-18-2-268-11-substantial-compliance" tabindex="-1">§ 18.2-268.11 Substantial compliance. <a class="header-anchor" href="#§-18-2-268-11-substantial-compliance" aria-label="Permalink to &quot;§ 18.2-268.11 Substantial compliance.&quot;">​</a></h1><p>The steps set forth in §§ <a href="http://law.lis.virginia.gov/vacode/18.2-268.2/" target="_blank" rel="noreferrer">18.2-268.2</a> through <a href="http://law.lis.virginia.gov/vacode/18.2-268.9/" target="_blank" rel="noreferrer">18.2-268.9</a> relating to taking, handling, identifying, and disposing of blood or breath samples are procedural and not substantive. Substantial compliance shall be sufficient. Failure to comply with any steps or portions thereof shall not of itself be grounds for finding the defendant not guilty, but shall go to the weight of the evidence and shall be considered with all the evidence in the case; however, the defendant shall have the right to introduce evidence on his own behalf to show noncompliance with the aforesaid procedures or any part thereof, and that as a result his rights were prejudiced.</p><p>1992, c. 830; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0933" target="_blank" rel="noreferrer">933</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0936" target="_blank" rel="noreferrer">936</a>.</p>',3),o=[r];function l(s,c,h,d,p,f){return a(),t("div",null,o)}const u=e(i,[["render",l]]);export{g as __pageData,u as default};
