import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 18.2-268.4 Trial and appeal for refusal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-268.4.md","filePath":"vacode/18.2-268.4.md"}'),i={name:"vacode/18.2-268.4.md"},o=t('<h1 id="§-18-2-268-4-trial-and-appeal-for-refusal" tabindex="-1">§ 18.2-268.4 Trial and appeal for refusal. <a class="header-anchor" href="#§-18-2-268-4-trial-and-appeal-for-refusal" aria-label="Permalink to &quot;§ 18.2-268.4 Trial and appeal for refusal.&quot;">​</a></h1><p>A. Venue for the trial of the warrant or summons shall lie in the court of the county or city in which the offense of driving under the influence of intoxicants or other offense listed in subsection A or B of § <a href="http://law.lis.virginia.gov/vacode/18.2-268.3/" target="_blank" rel="noreferrer">18.2-268.3</a> is to be tried.</p><p>B. The procedure for appeal and trial of any civil offense of § <a href="http://law.lis.virginia.gov/vacode/18.2-268.3/" target="_blank" rel="noreferrer">18.2-268.3</a> shall be the same as provided by law for misdemeanors; if requested by either party on appeal to the circuit court, trial by jury shall be as provided in Article 4 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-260/" target="_blank" rel="noreferrer">19.2-260</a> et seq.) of Chapter 15 of Title 19.2, and the Commonwealth shall be required to prove its case beyond a reasonable doubt.</p><p>C. If the defendant pleads guilty to a violation of § <a href="http://law.lis.virginia.gov/vacode/18.2-266/" target="_blank" rel="noreferrer">18.2-266</a> or <a href="http://law.lis.virginia.gov/vacode/18.2-266.1/" target="_blank" rel="noreferrer">18.2-266.1</a> or subsection B of § <a href="http://law.lis.virginia.gov/vacode/18.2-272/" target="_blank" rel="noreferrer">18.2-272</a> or of a similar ordinance, the court may dismiss the warrant or summons.</p><p>The court shall dispose of the defendant&#39;s license in accordance with the provisions of § <a href="http://law.lis.virginia.gov/vacode/46.2-398/" target="_blank" rel="noreferrer">46.2-398</a>; however, the defendant&#39;s license shall not be returned during any period of suspension imposed under § <a href="http://law.lis.virginia.gov/vacode/46.2-391.2/" target="_blank" rel="noreferrer">46.2-391.2</a>.</p><p>1992, c. 830; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0359" target="_blank" rel="noreferrer">359</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0363" target="_blank" rel="noreferrer">363</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0985" target="_blank" rel="noreferrer">985</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1013" target="_blank" rel="noreferrer">1013</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0757" target="_blank" rel="noreferrer">757</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0943" target="_blank" rel="noreferrer">943</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>.</p>',6),l=[o];function n(f,s,h,g,p,c){return r(),a("div",null,l)}const _=e(i,[["render",n]]);export{v as __pageData,_ as default};
