import{_ as e,o as r,c as o,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 18.2-286 Shooting in or across road or in street.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-286.md","filePath":"vacode/18.2-286.md"}'),t={name:"vacode/18.2-286.md"},i=a('<h1 id="§-18-2-286-shooting-in-or-across-road-or-in-street" tabindex="-1">§ 18.2-286 Shooting in or across road or in street. <a class="header-anchor" href="#§-18-2-286-shooting-in-or-across-road-or-in-street" aria-label="Permalink to &quot;§ 18.2-286 Shooting in or across road or in street.&quot;">​</a></h1><p>If any person discharges a firearm, crossbow, slingbow, arrowgun, or bow and arrow in or across any road, or within the right-of-way thereof, or in a street of any city or town, he shall, for each offense, be guilty of a Class 4 misdemeanor.</p><p>The provisions of this section shall not apply to firing ranges or shooting matches maintained, and supervised or approved, by law-enforcement officers and military personnel in performance of their lawful duties.</p><p>Code 1950, § 33.1-349; 1970, c. 322; 1975, cc. 14, 15; 1993, c. 322; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0018" target="_blank" rel="noreferrer">18</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0530" target="_blank" rel="noreferrer">530</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0557" target="_blank" rel="noreferrer">557</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0558" target="_blank" rel="noreferrer">558</a>.</p>',4),n=[i];function s(c,l,f,h,d,g){return r(),o("div",null,n)}const m=e(t,[["render",s]]);export{_ as __pageData,m as default};
