import{_ as e,o as r,c as o,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-308.2 Possession or transportation of firearms, firearms ammunition, stun weapons, explosives or concealed weapons by convicted felons; penalties; petition for restoration order; when issued.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-308.2.md","filePath":"vacode/18.2-308.2.md"}'),t={name:"vacode/18.2-308.2.md"},n=i('<h1 id="§-18-2-308-2-possession-or-transportation-of-firearms-firearms-ammunition-stun-weapons-explosives-or-concealed-weapons-by-convicted-felons-penalties-petition-for-restoration-order-when-issued" tabindex="-1">§ 18.2-308.2 Possession or transportation of firearms, firearms ammunition, stun weapons, explosives or concealed weapons by convicted felons; penalties; petition for restoration order; when issued. <a class="header-anchor" href="#§-18-2-308-2-possession-or-transportation-of-firearms-firearms-ammunition-stun-weapons-explosives-or-concealed-weapons-by-convicted-felons-penalties-petition-for-restoration-order-when-issued" aria-label="Permalink to &quot;§ 18.2-308.2 Possession or transportation of firearms, firearms ammunition, stun weapons, explosives or concealed weapons by convicted felons; penalties; petition for restoration order; when issued.&quot;">​</a></h1><p>A. It shall be unlawful for (i) any person who has been convicted of a felony; (ii) any person adjudicated delinquent as a juvenile 14 years of age or older at the time of the offense of murder in violation of § <a href="/vacode/18.2-31/">18.2-31</a> or <a href="/vacode/18.2-32/">18.2-32</a>, kidnapping in violation of § <a href="/vacode/18.2-47/">18.2-47</a>, robbery by the threat or presentation of firearms in violation of § <a href="/vacode/18.2-58/">18.2-58</a>, or rape in violation of § <a href="/vacode/18.2-61/">18.2-61</a>; or (iii) any person under the age of 29 who was adjudicated delinquent as a juvenile 14 years of age or older at the time of the offense of a delinquent act which would be a felony if committed by an adult, other than those felonies set forth in clause (ii), whether such conviction or adjudication occurred under the laws of the Commonwealth, or any other state, the District of Columbia, the United States or any territory thereof, to knowingly and intentionally possess or transport any firearm or ammunition for a firearm, any stun weapon as defined by § <a href="/vacode/18.2-308.1/">18.2-308.1</a>, or any explosive material, or to knowingly and intentionally carry about his person, hidden from common observation, any weapon described in subsection A of § <a href="/vacode/18.2-308/">18.2-308</a>. However, such person may possess in his residence or the curtilage thereof a stun weapon as defined by § <a href="/vacode/18.2-308.1/">18.2-308.1</a>. Any person who violates this section shall be guilty of a Class 6 felony. However, any person who violates this section by knowingly and intentionally possessing or transporting any firearm and who was previously convicted of a violent felony as defined in § <a href="/vacode/17.1-805/">17.1-805</a> shall be sentenced to a mandatory minimum term of imprisonment of five years. Any person who violates this section by knowingly and intentionally possessing or transporting any firearm and who was previously convicted of any other felony within the prior 10 years shall be sentenced to a mandatory minimum term of imprisonment of two years. The mandatory minimum terms of imprisonment prescribed for violations of this section shall be served consecutively with any other sentence.</p><p>B. The prohibitions of subsection A shall not apply to (i) any person who possesses a firearm, ammunition for a firearm, explosive material or other weapon while carrying out his duties as a member of the Armed Forces of the United States or of the National Guard of Virginia or of any other state, (ii) any law-enforcement officer in the performance of his duties, (iii) any person who has been pardoned or whose political disabilities have been removed pursuant to Article V, Section 12 of the Constitution of Virginia provided the Governor, in the document granting the pardon or removing the person&#39;s political disabilities, may expressly place conditions upon the reinstatement of the person&#39;s right to ship, transport, possess or receive firearms, (iv) any person whose right to possess firearms or ammunition has been restored under the law of another state subject to conditions placed upon the reinstatement of the person&#39;s right to ship, transport, possess, or receive firearms by such state, or (v) any person adjudicated delinquent as a juvenile who has completed a term of service of no less than two years in the Armed Forces of the United States and, if such person has been discharged from the Armed Forces of the United States, received an honorable discharge and who is not otherwise prohibited under clause (i) or (ii) of subsection A.</p><p>C. Any person prohibited from possessing, transporting, or carrying a firearm, ammunition for a firearm, or a stun weapon under subsection A may petition the circuit court of the jurisdiction in which he resides or, if the person is not a resident of the Commonwealth, the circuit court of any county or city where such person was last convicted of a felony or adjudicated delinquent of a disqualifying offense pursuant to subsection A, for a restoration order that unconditionally authorizes possessing, transporting, or carrying a firearm, ammunition for a firearm, or a stun weapon; however, no person who has been convicted of a felony shall be qualified to petition for such an order unless his civil rights have been restored by the Governor or other appropriate authority. A copy of the petition shall be mailed or delivered to the attorney for the Commonwealth for the jurisdiction where the petition was filed who shall be entitled to respond and represent the interests of the Commonwealth. The court shall conduct a hearing if requested by either party. The court may, in its discretion and for good cause shown, grant such petition and issue a restoration order. Such order shall contain the petitioner&#39;s name and date of birth. The clerk shall certify and forward forthwith to the Central Criminal Records Exchange (CCRE), on a form provided by the CCRE, a copy of the order to be accompanied by a complete set of the petitioner&#39;s fingerprints. The Department of State Police shall forthwith enter the petitioner&#39;s name and description in the CCRE so that the order&#39;s existence will be made known to law-enforcement personnel accessing the computerized criminal history records for investigative purposes. The provisions of this section relating to firearms, ammunition for a firearm, and stun weapons shall not apply to any person who has been issued a restoration order pursuant to this subsection.</p><p>C1. Any person who was prohibited from possessing, transporting or carrying explosive material under subsection A may possess, transport or carry such explosive material if his right to possess, transport or carry explosive material has been restored pursuant to federal law.</p><p>C2. The prohibitions of subsection A shall not prohibit any person other than a person convicted of an act of violence as defined in § <a href="/vacode/19.2-297.1/">19.2-297.1</a> or a violent felony as defined in subsection C of § <a href="/vacode/17.1-805/">17.1-805</a> from possessing, transporting, or carrying (i) antique firearms or (ii) black powder in a quantity not exceeding five pounds if it is intended to be used solely for sporting, recreational, or cultural purposes in antique firearms. For the purposes of this subsection, &quot;antique firearms&quot; means any firearm described in subdivision 3 of the definition of &quot;antique firearm&quot; in subsection F of § <a href="/vacode/18.2-308.2:2/">18.2-308.2:2</a>.</p><p>D. For the purpose of this section:</p><p>&quot;Ammunition for a firearm&quot; means the combination of a cartridge, projectile, primer, or propellant designed for use in a firearm other than an antique firearm as defined in § <a href="/vacode/18.2-308.2:2/">18.2-308.2:2</a>.</p><p>&quot;Explosive material&quot; means any chemical compound mixture, or device, the primary or common purpose of which is to function by explosion; the term includes, but is not limited to, dynamite and other high explosives, black powder, pellet powder, smokeless gun powder, detonators, blasting caps and detonating cord but shall not include fireworks or permissible fireworks as defined in § <a href="/vacode/27-95/">27-95</a>.</p><p>1979, c. 474; 1982, c. 515; 1983, c. 233; 1986, cc. 409, 641; 1987, c. 108; 1988, c. 237; 1989, cc. 514, 531; 1993, cc. 468, 926; 1994, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0859" target="_blank" rel="noreferrer">859</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0949" target="_blank" rel="noreferrer">949</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0811" target="_blank" rel="noreferrer">811</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0854" target="_blank" rel="noreferrer">854</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0362" target="_blank" rel="noreferrer">362</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0110" target="_blank" rel="noreferrer">110</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0429" target="_blank" rel="noreferrer">429</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0461" target="_blank" rel="noreferrer">461</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0600" target="_blank" rel="noreferrer">600</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0833" target="_blank" rel="noreferrer">833</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0519" target="_blank" rel="noreferrer">519</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0752" target="_blank" rel="noreferrer">752</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0236" target="_blank" rel="noreferrer">236</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0781" target="_blank" rel="noreferrer">781</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0200" target="_blank" rel="noreferrer">200</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0337" target="_blank" rel="noreferrer">337</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0203" target="_blank" rel="noreferrer">203</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1111" target="_blank" rel="noreferrer">1111</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1112" target="_blank" rel="noreferrer">1112</a>.</p>',10),a=[n];function s(l,f,h,c,p,d){return r(),o("div",null,a)}const m=e(t,[["render",s]]);export{g as __pageData,m as default};
