import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-308.2:01 Possession or transportation of certain firearms by certain persons.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-308.2:01.md","filePath":"vacode/18.2-308.2:01.md"}'),n={name:"vacode/18.2-308.2:01.md"},r=t('<h1 id="§-18-2-308-2-01-possession-or-transportation-of-certain-firearms-by-certain-persons" tabindex="-1">§ 18.2-308.2:01 Possession or transportation of certain firearms by certain persons. <a class="header-anchor" href="#§-18-2-308-2-01-possession-or-transportation-of-certain-firearms-by-certain-persons" aria-label="Permalink to &quot;§ 18.2-308.2:01 Possession or transportation of certain firearms by certain persons.&quot;">​</a></h1><p>A. It shall be unlawful for any person who is not a citizen of the United States or who is not a person lawfully admitted for permanent residence to knowingly and intentionally possess or transport any assault firearm or to knowingly and intentionally carry about his person, hidden from common observation, an assault firearm.</p><p>B. It shall be unlawful for any person who is not a citizen of the United States and who is not lawfully present in the United States to knowingly and intentionally possess or transport any firearm or to knowingly and intentionally carry about his person, hidden from common observation, any firearm. A violation of this section shall be punishable as a Class 6 felony.</p><p>C. For purposes of this section, &quot;assault firearm&quot; means any semi-automatic center-fire rifle or pistol that expels single or multiple projectiles by action of an explosion of a combustible material and is equipped at the time of the offense with a magazine which will hold more than 20 rounds of ammunition or designed by the manufacturer to accommodate a silencer or equipped with a folding stock.</p><p>1993, c. 674; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0976" target="_blank" rel="noreferrer">976</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0347" target="_blank" rel="noreferrer">347</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0408" target="_blank" rel="noreferrer">408</a>.</p>',5),i=[r];function s(l,c,p,f,h,d){return o(),a("div",null,i)}const _=e(n,[["render",s]]);export{u as __pageData,_ as default};
