import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-308.4 Possession of firearms while in possession of certain substances.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-308.4.md","filePath":"vacode/18.2-308.4.md"}'),s={name:"vacode/18.2-308.4.md"},r=t('<h1 id="§-18-2-308-4-possession-of-firearms-while-in-possession-of-certain-substances" tabindex="-1">§ 18.2-308.4 Possession of firearms while in possession of certain substances. <a class="header-anchor" href="#§-18-2-308-4-possession-of-firearms-while-in-possession-of-certain-substances" aria-label="Permalink to &quot;§ 18.2-308.4 Possession of firearms while in possession of certain substances.&quot;">​</a></h1><p>A. It shall be unlawful for any person unlawfully in possession of a controlled substance classified in Schedule I or II of the Drug Control Act (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3400/" target="_blank" rel="noreferrer">54.1-3400</a> et seq.) of Title 54.1 to simultaneously with knowledge and intent possess any firearm. A violation of this subsection is a Class 6 felony and constitutes a separate and distinct felony.</p><p>B. It shall be unlawful for any person unlawfully in possession of a controlled substance classified in Schedule I or II of the Drug Control Act (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3400/" target="_blank" rel="noreferrer">54.1-3400</a> et seq.) to simultaneously with knowledge and intent possess any firearm on or about his person. A violation of this subsection is a Class 6 felony and constitutes a separate and distinct felony and any person convicted hereunder shall be sentenced to a mandatory minimum term of imprisonment of two years. Such punishment shall be separate and apart from, and shall be made to run consecutively with, any punishment received for the commission of the primary felony.</p><p>C. It shall be unlawful for any person to possess, use, or attempt to use any pistol, shotgun, rifle, or other firearm or display such weapon in a threatening manner while committing or attempting to commit the illegal manufacture, sale, distribution, or the possession with the intent to manufacture, sell, or distribute a controlled substance classified in Schedule I or Schedule II of the Drug Control Act (§ <a href="http://law.lis.virginia.gov/vacode/54.1-3400/" target="_blank" rel="noreferrer">54.1-3400</a> et seq.) or more than one pound of marijuana. A violation of this subsection is a Class 6 felony, and constitutes a separate and distinct felony and any person convicted hereunder shall be sentenced to a mandatory minimum term of imprisonment of five years. Such punishment shall be separate and apart from, and shall be made to run consecutively with, any punishment received for the commission of the primary felony.</p><p>1987, c. 285; 1990, c. 625; 1992, c. 707; 1993, c. 831; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0949" target="_blank" rel="noreferrer">949</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0461" target="_blank" rel="noreferrer">461</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0384" target="_blank" rel="noreferrer">384</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0410" target="_blank" rel="noreferrer">410</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0719" target="_blank" rel="noreferrer">719</a>.</p>',5),i=[r];function o(l,c,f,h,p,u){return a(),n("div",null,i)}const m=e(s,[["render",o]]);export{g as __pageData,m as default};
