import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-32 First and second degree murder defined; punishment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-32.md","filePath":"vacode/18.2-32.md"}'),n={name:"vacode/18.2-32.md"},i=t('<h1 id="§-18-2-32-first-and-second-degree-murder-defined-punishment" tabindex="-1">§ 18.2-32 First and second degree murder defined; punishment. <a class="header-anchor" href="#§-18-2-32-first-and-second-degree-murder-defined-punishment" aria-label="Permalink to &quot;§ 18.2-32 First and second degree murder defined; punishment.&quot;">​</a></h1><p>Murder, other than aggravated murder, by poison, lying in wait, imprisonment, starving, or by any willful, deliberate, and premeditated killing, or in the commission of, or attempt to commit, arson, rape, forcible sodomy, inanimate or animate object sexual penetration, robbery, burglary or abduction, except as provided in § <a href="/vacode/18.2-31/">18.2-31</a>, is murder of the first degree, punishable as a Class 2 felony.</p><p>All murder other than aggravated murder and murder in the first degree is murder of the second degree and is punishable by confinement in a state correctional facility for not less than five nor more than forty years.</p><p>Code 1950, § 18.1-21; 1960, c. 358; 1962, c. 42; 1975, cc. 14, 15; 1976, c. 503; 1977, cc. 478, 492; 1981, c. 397; 1993, cc. 463, 490; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0281" target="_blank" rel="noreferrer">281</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>.</p>',4),o=[i];function d(s,c,l,f,m,p){return r(),a("div",null,o)}const g=e(n,[["render",d]]);export{u as __pageData,g as default};
