import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 18.2-323.1 Drinking while operating a motor vehicle; possession of open container while operating a motor vehicle and presumption; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-323.1.md","filePath":"vacode/18.2-323.1.md"}'),n={name:"vacode/18.2-323.1.md"},t=i('<h1 id="§-18-2-323-1-drinking-while-operating-a-motor-vehicle-possession-of-open-container-while-operating-a-motor-vehicle-and-presumption-penalty" tabindex="-1">§ 18.2-323.1 Drinking while operating a motor vehicle; possession of open container while operating a motor vehicle and presumption; penalty. <a class="header-anchor" href="#§-18-2-323-1-drinking-while-operating-a-motor-vehicle-possession-of-open-container-while-operating-a-motor-vehicle-and-presumption-penalty" aria-label="Permalink to &quot;§ 18.2-323.1 Drinking while operating a motor vehicle; possession of open container while operating a motor vehicle and presumption; penalty.&quot;">​</a></h1><p>A. It is unlawful for any person to consume an alcoholic beverage while driving a motor vehicle upon a public highway of the Commonwealth.</p><p>B. Unless the driver is delivering alcoholic beverages in accordance with the provisions of § <a href="/vacode/4.1-212.1/">4.1-212.1</a>, a rebuttable presumption that the driver has consumed an alcoholic beverage in violation of this section shall be created if (i) an open container is located within the passenger area of the motor vehicle, (ii) the alcoholic beverage in the open container has been at least partially removed, and (iii) the appearance, conduct, odor of alcohol, speech, or other physical characteristic of the driver of the motor vehicle may be reasonably associated with the consumption of an alcoholic beverage.</p><p>C. For the purposes of this section:</p><p>&quot;Open container&quot; means any vessel containing an alcoholic beverage, except the originally sealed manufacturer&#39;s container.</p><p>&quot;Passenger area&quot; means the area designed to seat the driver of any motor vehicle, any area within the reach of the driver, including an unlocked glove compartment, and the area designed to seat passengers. &quot;Passenger area&quot; does not include the trunk of any passenger vehicle, the area behind the last upright seat of a passenger van, station wagon, hatchback, sport utility vehicle, or any similar vehicle, the living quarters of a motor home, or the passenger area of a motor vehicle designed, maintained, or used primarily for the transportation of persons for compensation, including a bus, taxi, or limousine, while engaged in the transportation of such persons.</p><p>D. A violation of this section is punishable as a Class 4 misdemeanor.</p><p>1989, c. 343; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0890" target="_blank" rel="noreferrer">890</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0078" target="_blank" rel="noreferrer">78</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>.</p>',8),r=[t];function s(l,h,c,p,d,g){return a(),o("div",null,r)}const f=e(n,[["render",s]]);export{v as __pageData,f as default};
