import{_ as e,o as t,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-331.1 Operation of gambling devices at unregulated locations; civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-331.1.md","filePath":"vacode/18.2-331.1.md"}'),i={name:"vacode/18.2-331.1.md"},o=n('<h1 id="§-18-2-331-1-operation-of-gambling-devices-at-unregulated-locations-civil-penalty" tabindex="-1">§ 18.2-331.1 Operation of gambling devices at unregulated locations; civil penalty. <a class="header-anchor" href="#§-18-2-331-1-operation-of-gambling-devices-at-unregulated-locations-civil-penalty" aria-label="Permalink to &quot;§ 18.2-331.1 Operation of gambling devices at unregulated locations; civil penalty.&quot;">​</a></h1><p>A. In addition to any other penalty provided by law, any person who conducts, finances, manages, supervises, directs, sells, or owns a gambling device that is located in an unregulated location is subject to a civil penalty of up to $25,000 for each gambling device located in such unregulated location.</p><p>B. The Attorney General, an attorney for the Commonwealth, or the attorney for any locality may cause an action in equity to be brought in the name of the Commonwealth or of the locality, as applicable, to immediately enjoin the operation of a gambling device in violation of this section and to request an attachment against all such devices and any moneys within such devices pursuant to Chapter 20 (§ <a href="/vacode/8.01-533/">8.01-533</a> et seq.) of Title 8.01, and to recover the civil penalty of up to $25,000 per device.</p><p>C. In any action brought under this section, the Attorney General, the attorney for the Commonwealth, or the attorney for the locality may recover reasonable expenses incurred by the state or local agency in investigating and preparing the case, and attorney fees.</p><p>D. Any civil penalties assessed under this section in an action in equity brought in the name of the Commonwealth shall be paid into the Literary Fund. Any civil penalties assessed under this section in an action in equity brought in the name of a locality shall be paid into the general fund of the locality.</p><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0329" target="_blank" rel="noreferrer">329</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0546" target="_blank" rel="noreferrer">546</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0553" target="_blank" rel="noreferrer">553</a>.</p>',6),r=[o];function l(c,s,h,d,p,g){return t(),a("div",null,r)}const v=e(i,[["render",l]]);export{f as __pageData,v as default};
