import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-338 Enforcement of § 18.2-331 by Governor and Attorney General.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-338.md","filePath":"vacode/18.2-338.md"}'),r={name:"vacode/18.2-338.md"},a=n('<h1 id="§-18-2-338-enforcement-of-§-18-2-331-by-governor-and-attorney-general" tabindex="-1">§ 18.2-338 Enforcement of § 18.2-331 by Governor and Attorney General. <a class="header-anchor" href="#§-18-2-338-enforcement-of-§-18-2-331-by-governor-and-attorney-general" aria-label="Permalink to &quot;§ 18.2-338 Enforcement of § 18.2-331 by Governor and Attorney General.&quot;">​</a></h1><p>If it shall come to the knowledge of the Governor that § <a href="http://law.lis.virginia.gov/vacode/18.2-331/" target="_blank" rel="noreferrer">18.2-331</a> is not being enforced in any county, city or town, the Governor may call upon the Attorney General to direct its enforcement in such county, city or town, and thereupon the Attorney General may instruct the attorney for the Commonwealth, sheriff and chief of police, if any, of such county, or the attorney for the Commonwealth and chief of police of such city, or the attorney for the Commonwealth of the county in which such town is located and the chief of police or sergeant of such town, to take such steps as may be necessary to insure the enforcement of such section in such county, city or town, and if any such officers, after receiving such instructions, shall thereafter fail or refuse to exercise diligence in the enforcement of § <a href="http://law.lis.virginia.gov/vacode/18.2-331/" target="_blank" rel="noreferrer">18.2-331</a>, the Attorney General shall make report thereof in writing to the Governor and to the judge of the circuit court having jurisdiction over the acts thereby prohibited, and thereupon the Attorney General upon being directed so to do by the Governor, shall take such steps as he may deem proper in directing the institution and prosecution of criminal proceedings, to secure the enforcement of § <a href="http://law.lis.virginia.gov/vacode/18.2-331/" target="_blank" rel="noreferrer">18.2-331</a>.</p><p>Code 1950, § 18.1-334; 1960, c. 358; 1975, cc. 14, 15.</p>',3),c=[a];function i(h,s,f,l,d,u){return t(),o("div",null,c)}const p=e(r,[["render",i]]);export{m as __pageData,p as default};
