import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse(`{"title":"§ 18.2-340.28:2 Conduct of Texas Hold'em poker tournaments by qualified organizations; limitation of operator fee; conditions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-340.28:2.md","filePath":"vacode/18.2-340.28:2.md"}`),n={name:"vacode/18.2-340.28:2.md"},i=t('<h1 id="§-18-2-340-28-2-conduct-of-texas-hold-em-poker-tournaments-by-qualified-organizations-limitation-of-operator-fee-conditions" tabindex="-1">§ 18.2-340.28:2 Conduct of Texas Hold&#39;em poker tournaments by qualified organizations; limitation of operator fee; conditions. <a class="header-anchor" href="#§-18-2-340-28-2-conduct-of-texas-hold-em-poker-tournaments-by-qualified-organizations-limitation-of-operator-fee-conditions" aria-label="Permalink to &quot;§ 18.2-340.28:2 Conduct of Texas Hold&#39;em poker tournaments by qualified organizations; limitation of operator fee; conditions.&quot;">​</a></h1><p>A. Any organization qualified to conduct bingo games on or after July 1, 2019, may conduct Texas Hold&#39;em poker tournaments; however, no such organization may conduct individual Texas Hold&#39;em poker games. The Commissioner shall promulgate regulations establishing circumstances under which organizations qualified to conduct bingo games prior to July 1, 2019, may conduct Texas Hold&#39;em poker tournaments.</p><p>B. A qualified organization may contract with an operator to administer Texas Hold&#39;em poker tournaments. Limitations on operator fees shall be established by Department regulations.</p><p>C. A qualified organization shall accept only cash or, at its option, checks in payment of any charges or assessments for players to participate in Texas Hold&#39;em poker tournaments. However, no such organization shall accept postdated checks in payment of any charges or assessments for players to participate in Texas Hold&#39;em poker tournaments.</p><p>D. No qualified organization or any person on the premises shall extend lines of credit or accept any credit or debit card or other electronic fund transfer in payment of any charges or assessments for players to participate in Texas Hold&#39;em poker tournaments.</p><p>E. No qualified organization shall allow any individual younger than 18 years of age to participate in Texas Hold&#39;em poker tournaments.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0982" target="_blank" rel="noreferrer">982</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0609" target="_blank" rel="noreferrer">609</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0612" target="_blank" rel="noreferrer">612</a>.</p>',7),r=[i];function s(l,c,p,d,m,f){return o(),a("div",null,r)}const h=e(n,[["render",s]]);export{g as __pageData,h as default};
