import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-340.29 Joint operation of bingo games; written reports; joint permit required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-340.29.md","filePath":"vacode/18.2-340.29.md"}'),r={name:"vacode/18.2-340.29.md"},n=i('<h1 id="§-18-2-340-29-joint-operation-of-bingo-games-written-reports-joint-permit-required" tabindex="-1">§ 18.2-340.29 Joint operation of bingo games; written reports; joint permit required. <a class="header-anchor" href="#§-18-2-340-29-joint-operation-of-bingo-games-written-reports-joint-permit-required" aria-label="Permalink to &quot;§ 18.2-340.29 Joint operation of bingo games; written reports; joint permit required.&quot;">​</a></h1><p>A. Any two or more qualified organizations may jointly organize and conduct bingo games provided both have fully complied with all other provisions of this article.</p><p>B. Any two or more qualified organizations jointly conducting such games shall be (i) subject to the same restrictions and prohibitions contained in this article that would apply to a single organization conducting bingo games and (ii) required to furnish to the Department a written report setting forth the location where such games will be held, the division of manpower, costs, and proceeds for each game to be jointly conducted.</p><p>Upon a finding that the division of manpower and costs for each game bears a reasonable relationship to the division of proceeds, the Department shall issue a joint permit.</p><p>C. No bingo game shall be jointly conducted until the joint permit issued pursuant to subsection B is obtained by the organizations.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0837" target="_blank" rel="noreferrer">837</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0884" target="_blank" rel="noreferrer">884</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>.</p>',6),a=[n];function s(p,l,c,d,h,g){return t(),o("div",null,a)}const u=e(r,[["render",s]]);export{f as __pageData,u as default};
