import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 18.2-340.30:2 Reports of electronic gaming adjusted gross receipts by electronic gaming manufacturer required; form of reports; failure to file.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-340.30:2.md","filePath":"vacode/18.2-340.30:2.md"}'),o={name:"vacode/18.2-340.30:2.md"},i=a('<h1 id="§-18-2-340-30-2-reports-of-electronic-gaming-adjusted-gross-receipts-by-electronic-gaming-manufacturer-required-form-of-reports-failure-to-file" tabindex="-1">§ 18.2-340.30:2 Reports of electronic gaming adjusted gross receipts by electronic gaming manufacturer required; form of reports; failure to file. <a class="header-anchor" href="#§-18-2-340-30-2-reports-of-electronic-gaming-adjusted-gross-receipts-by-electronic-gaming-manufacturer-required-form-of-reports-failure-to-file" aria-label="Permalink to &quot;§ 18.2-340.30:2 Reports of electronic gaming adjusted gross receipts by electronic gaming manufacturer required; form of reports; failure to file.&quot;">​</a></h1><p>A. Each electronic gaming manufacturer that holds a permit issued by the Department pursuant to § <a href="/vacode/18.2-340.34/">18.2-340.34</a> shall keep a complete record of all electronic gaming adjusted gross receipts and shall file at least annually, on a form prescribed by the Department, a report of all such receipts and any other information related to the manufacture of electronic gaming devices that the Department may require.</p><p>B. The report required by this section shall be filed on or before the date prescribed by the Department. The Board, by regulation, shall establish a schedule of late fees to be assessed for any electronic gaming manufacturer that fails to submit required reports by the due date.</p><p>C. Each electronic gaming manufacturer shall maintain for three years a complete written record of all electronic gaming adjusted gross receipts.</p><p>D. The failure to file the report required by this section within 30 days of the time such report is due shall cause the automatic revocation of the electronic gaming manufacturer&#39;s permit, and no such manufacturer shall manufacture any new electronic gaming device until the report is properly filed and a new permit is obtained. However, the Department may grant an extension of time for filing such report for a period not to exceed 45 days if requested by a manufacturer, provided that the manufacturer requests an extension within 15 days of the time such report is due and all projected fees are paid. For the term of any such extension, the manufacturer&#39;s permit shall not be automatically revoked, such manufacturer may continue to manufacture electronic gaming devices, and no new permit shall be required.</p><p>E. For purposes of this section, the requirement to file a report shall also include the payment of any applicable fees required to accompany such report.</p><p>2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0722" target="_blank" rel="noreferrer">722</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>.</p>',7),n=[i];function s(c,l,u,f,d,p){return r(),t("div",null,n)}const g=e(o,[["render",s]]);export{h as __pageData,g as default};
