import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-340.31 Audit of reports; exemption; audit and administration fee; additional assessment of gross receipts and electronic gaming adjusted gross receipts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-340.31.md","filePath":"vacode/18.2-340.31.md"}'),i={name:"vacode/18.2-340.31.md"},n=r('<h1 id="§-18-2-340-31-audit-of-reports-exemption-audit-and-administration-fee-additional-assessment-of-gross-receipts-and-electronic-gaming-adjusted-gross-receipts" tabindex="-1">§ 18.2-340.31 Audit of reports; exemption; audit and administration fee; additional assessment of gross receipts and electronic gaming adjusted gross receipts. <a class="header-anchor" href="#§-18-2-340-31-audit-of-reports-exemption-audit-and-administration-fee-additional-assessment-of-gross-receipts-and-electronic-gaming-adjusted-gross-receipts" aria-label="Permalink to &quot;§ 18.2-340.31 Audit of reports; exemption; audit and administration fee; additional assessment of gross receipts and electronic gaming adjusted gross receipts.&quot;">​</a></h1><p>A. All reports filed pursuant to §§ <a href="/vacode/18.2-340.30/">18.2-340.30</a> and <a href="http://law.lis.virginia.gov/vacode/title18.2/chapter8/section18.2-340.30:2/" target="_blank" rel="noreferrer">18.2-340.30:2</a> shall be subject to audit by the Department in accordance with Department regulations. The Department may engage the services of independent certified public accountants to perform any audits deemed necessary to fulfill the Department&#39;s responsibilities under this article.</p><p>B. The Department shall prescribe a reasonable audit and administration fee to be paid by (i) any organization conducting charitable gaming under a permit issued by the Department unless the organization is exempt from such fee pursuant to § <a href="/vacode/18.2-340.23/">18.2-340.23</a> or (ii) any electronic gaming manufacturer that holds a permit issued by the Department pursuant to § <a href="/vacode/18.2-340.34/">18.2-340.34</a>. Such fee shall not exceed one-half of one percent of the gross receipts that an organization reports pursuant to § <a href="/vacode/18.2-340.30/">18.2-340.30</a> or one-half of one percent of the electronic gaming adjusted gross receipts that an electronic gaming manufacturer reports pursuant to § <a href="http://law.lis.virginia.gov/vacode/title18.2/chapter8/section18.2-340.30:2/" target="_blank" rel="noreferrer">18.2-340.30:2</a>. The audit and administration fee shall accompany each report for each calendar quarter.</p><p>C. The audit and administration fee shall be payable to the Treasurer of Virginia. All such fees received by the Treasurer of Virginia shall be separately accounted for and shall be used only by the Department for the purposes of auditing and regulating charitable gaming.</p><p>D. In addition to the fee imposed under subsection B, an additional fee of (i) one-quarter of one percent of the gross receipts that an organization reports pursuant to § <a href="/vacode/18.2-340.30/">18.2-340.30</a> shall be paid by the organization or (ii) one-quarter of one percent of the electronic gaming adjusted gross receipts that an electronic gaming manufacturer reports pursuant to § <a href="http://law.lis.virginia.gov/vacode/title18.2/chapter8/section18.2-340.30:2/" target="_blank" rel="noreferrer">18.2-340.30:2</a> shall be paid by the electronic gaming manufacturer to the Treasurer of Virginia. All such amounts shall be collected and deposited in the same manner as prescribed in subsections B and C and shall be used for the same purposes.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0837" target="_blank" rel="noreferrer">837</a>; 1997, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0838" target="_blank" rel="noreferrer">838</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0884" target="_blank" rel="noreferrer">884</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0644" target="_blank" rel="noreferrer">644</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0982" target="_blank" rel="noreferrer">982</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0609" target="_blank" rel="noreferrer">609</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0722" target="_blank" rel="noreferrer">722</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>.</p>',6),o=[n];function s(l,c,d,p,g,f){return a(),t("div",null,o)}const m=e(i,[["render",s]]);export{u as __pageData,m as default};
