import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-340.36:1 Civil penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-340.36:1.md","filePath":"vacode/18.2-340.36:1.md"}'),r={name:"vacode/18.2-340.36:1.md"},o=a('<h1 id="§-18-2-340-36-1-civil-penalty" tabindex="-1">§ 18.2-340.36:1 Civil penalty. <a class="header-anchor" href="#§-18-2-340-36-1-civil-penalty" aria-label="Permalink to &quot;§ 18.2-340.36:1 Civil penalty.&quot;">​</a></h1><p>A. Any person or organization, whether permitted or qualified pursuant to this article or not, that (i) conducts charitable gaming without first obtaining a permit to do so, (ii) continues to conduct such games after revocation or suspension of such permit, or (iii) otherwise violates any provision of this article shall, in addition to any other penalties provided, be subject to a civil penalty of not less than $25,000 and not more than $50,000 per incident. Any civil penalties collected pursuant to this section shall be payable to the State Treasurer for remittance to the Department.</p><p>B. Any electronic gaming manufacturer, whether permitted pursuant to this article or not, shall, in addition to any other penalties provided, be subject to the penalty identified in subsection A for any violation of any provision of this article.</p><p>2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0608" target="_blank" rel="noreferrer">608</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0722" target="_blank" rel="noreferrer">722</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>.</p>',4),n=[o];function l(s,c,p,h,d,_){return t(),i("div",null,n)}const g=e(r,[["render",l]]);export{u as __pageData,g as default};
