import{_ as e,o as i,c as a,O as n}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-340 County ordinances prohibiting illegal gambling.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-340.md","filePath":"vacode/18.2-340.md"}'),o={name:"vacode/18.2-340.md"},t=n('<h1 id="§-18-2-340-county-ordinances-prohibiting-illegal-gambling" tabindex="-1">§ 18.2-340 County ordinances prohibiting illegal gambling. <a class="header-anchor" href="#§-18-2-340-county-ordinances-prohibiting-illegal-gambling" aria-label="Permalink to &quot;§ 18.2-340 County ordinances prohibiting illegal gambling.&quot;">​</a></h1><p>The governing body of any county may adopt ordinances prohibiting illegal gambling, including a provision for forfeiture proceedings in accordance with Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2. Such ordinances shall not conflict with the provisions of this article or with other state laws and any penalties provided for violation of such ordinances shall not exceed a fine of $2,500 or confinement in jail for 12 months, either or both.</p><p>Code 1950, § 18.1-344; 1960, c. 358; 1975, cc. 14, 15; 1991, c. 710; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',3),r=[t];function l(c,s,g,d,h,p){return i(),a("div",null,r)}const b=e(o,[["render",l]]);export{f as __pageData,b as default};
