import{_ as e,o as t,c as o,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-348.1 Promoting travel for prostitution; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-348.1.md","filePath":"vacode/18.2-348.1.md"}'),n={name:"vacode/18.2-348.1.md"},i=a('<h1 id="§-18-2-348-1-promoting-travel-for-prostitution-penalty" tabindex="-1">§ 18.2-348.1 Promoting travel for prostitution; penalty. <a class="header-anchor" href="#§-18-2-348-1-promoting-travel-for-prostitution-penalty" aria-label="Permalink to &quot;§ 18.2-348.1 Promoting travel for prostitution; penalty.&quot;">​</a></h1><p>It is unlawful for any travel agent to knowingly promote travel services, as defined in § <a href="/vacode/59.1-445/">59.1-445</a>, for the purposes of prostitution or any act in violation of an offense set forth in subdivision 1 of the definition of Tier III offense as defined in § <a href="/vacode/9.1-902/">9.1-902</a>, made punishable within the Commonwealth, whether committed within or without. Violation of this section shall constitute a separate and distinct offense, and any person violating this section is guilty of a Class 1 misdemeanor. Punishment for a violation of this section shall be separate and apart from any punishment received from any other offense. For the purposes of this section &quot;travel agent&quot; means any person who for a consideration consults with or advises persons concerning travel services in the course of his business.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0458" target="_blank" rel="noreferrer">458</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>.</p>',3),r=[i];function s(l,f,c,p,h,d){return t(),o("div",null,r)}const m=e(n,[["render",s]]);export{u as __pageData,m as default};
