import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-348 Aiding prostitution or illicit sexual intercourse, etc.; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-348.md","filePath":"vacode/18.2-348.md"}'),r={name:"vacode/18.2-348.md"},a=i('<h1 id="§-18-2-348-aiding-prostitution-or-illicit-sexual-intercourse-etc-penalty" tabindex="-1">§ 18.2-348 Aiding prostitution or illicit sexual intercourse, etc.; penalty. <a class="header-anchor" href="#§-18-2-348-aiding-prostitution-or-illicit-sexual-intercourse-etc-penalty" aria-label="Permalink to &quot;§ 18.2-348 Aiding prostitution or illicit sexual intercourse, etc.; penalty.&quot;">​</a></h1><p>It is unlawful for any person or any officer, employee, or agent of any firm, association, or corporation with knowledge of, or good reason to believe, the immoral purpose of such visit, to take or transport or assist in taking or transporting, or offer to take or transport on foot or in any way, any person to a place, whether within or outside any building or structure, used or to be used for the purpose of lewdness, assignation, or prostitution within the Commonwealth or to procure or assist in procuring for the purpose of illicit sexual intercourse, anal intercourse, cunnilingus, fellatio, or anilingus or any act violative of § <a href="/vacode/18.2-361/">18.2-361</a>, or touching of the unclothed genitals or anus of another person with the intent to sexually arouse or gratify, or to give any information or direction to any person with intent to enable such person to commit an act of prostitution. A violation of this section is a Class 1 misdemeanor. However, any adult who violates this section with a person under the age of 18 is guilty of a Class 6 felony.</p><p>Code 1950, § 18.1-197; 1960, c. 358; 1975, cc. 14, 15; 1980, c. 534; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0617" target="_blank" rel="noreferrer">617</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0595" target="_blank" rel="noreferrer">595</a>.</p>',3),n=[a];function s(l,c,u,p,f,h){return o(),t("div",null,n)}const _=e(r,[["render",s]]);export{g as __pageData,_ as default};
