import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 18.2-355 Taking, detaining, etc., person for prostitution, etc., or consenting thereto; human trafficking.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-355.md","filePath":"vacode/18.2-355.md"}'),n={name:"vacode/18.2-355.md"},a=t('<h1 id="§-18-2-355-taking-detaining-etc-person-for-prostitution-etc-or-consenting-thereto-human-trafficking" tabindex="-1">§ 18.2-355 Taking, detaining, etc., person for prostitution, etc., or consenting thereto; human trafficking. <a class="header-anchor" href="#§-18-2-355-taking-detaining-etc-person-for-prostitution-etc-or-consenting-thereto-human-trafficking" aria-label="Permalink to &quot;§ 18.2-355 Taking, detaining, etc., person for prostitution, etc., or consenting thereto; human trafficking.&quot;">​</a></h1><p>Any person who:</p><p>(1) For purposes of prostitution or unlawful sexual intercourse, takes any person into, or persuades, encourages or causes any person to enter, a bawdy place, or takes or causes such person to be taken to any place against his or her will for such purposes; or</p><p>(2) Takes or detains a person against his or her will with the intent to compel such person, by force, threats, persuasions, menace or duress, to marry him or her or to marry any other person, or to be defiled; or</p><p>(3) Being parent, guardian, legal custodian or one standing in loco parentis of a person, consents to such person being taken or detained by any person for the purpose of prostitution or unlawful sexual intercourse; or</p><p>(4) For purposes of prostitution, takes any minor into, or persuades, encourages, or causes any minor to enter, a bawdy place, or takes or causes such person to be taken to any place for such purposes; is guilty of pandering.</p><p>A violation of subdivision (1), (2), or (3) is punishable as a Class 4 felony. A violation of subdivision (4) is punishable as a Class 3 felony.</p><p>Code 1950, § 18.1-204; 1960, c. 358; 1975, cc. 14, 15; 1980, c. 534; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0555" target="_blank" rel="noreferrer">555</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0649" target="_blank" rel="noreferrer">649</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0706" target="_blank" rel="noreferrer">706</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0395" target="_blank" rel="noreferrer">395</a>.</p>',8),i=[a];function s(c,p,l,u,f,g){return o(),r("div",null,i)}const _=e(n,[["render",s]]);export{d as __pageData,_ as default};
