import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-357.1 Commercial sex trafficking; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-357.1.md","filePath":"vacode/18.2-357.1.md"}'),o={name:"vacode/18.2-357.1.md"},r=i('<h1 id="§-18-2-357-1-commercial-sex-trafficking-penalties" tabindex="-1">§ 18.2-357.1 Commercial sex trafficking; penalties. <a class="header-anchor" href="#§-18-2-357-1-commercial-sex-trafficking-penalties" aria-label="Permalink to &quot;§ 18.2-357.1 Commercial sex trafficking; penalties.&quot;">​</a></h1><p>A. Any person who, with the intent to receive money or other valuable thing or to assist another in receiving money or other valuable thing from the earnings of a person from prostitution or unlawful sexual intercourse in violation of § <a href="/vacode/18.2-346/">18.2-346</a>, solicits, invites, recruits, encourages, or otherwise causes or attempts to cause a person to violate § <a href="/vacode/18.2-346/">18.2-346</a> is guilty of a Class 5 felony.</p><p>B. Any person who violates subsection A through the use of force, intimidation, or deception is guilty of a Class 4 felony.</p><p>C. Any adult who violates subsection A with a person under 18 years of age is guilty of a Class 3 felony.</p><p>D. Each violation of this section constitutes a separate and distinct felony.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0690" target="_blank" rel="noreferrer">690</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0691" target="_blank" rel="noreferrer">691</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0617" target="_blank" rel="noreferrer">617</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0188" target="_blank" rel="noreferrer">188</a>.</p>',6),n=[r];function s(l,c,f,p,h,g){return a(),t("div",null,n)}const d=e(o,[["render",s]]);ex