import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 18.2-361.1 Victims of sex trafficking; affirmative defense.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-361.1.md","filePath":"vacode/18.2-361.1.md"}'),a={name:"vacode/18.2-361.1.md"},n=o('<h1 id="§-18-2-361-1-victims-of-sex-trafficking-affirmative-defense" tabindex="-1">§ 18.2-361.1 Victims of sex trafficking; affirmative defense. <a class="header-anchor" href="#§-18-2-361-1-victims-of-sex-trafficking-affirmative-defense" aria-label="Permalink to &quot;§ 18.2-361.1 Victims of sex trafficking; affirmative defense.&quot;">​</a></h1><p>A. For the purposes of this section:</p><p>&quot;Qualifying offense&quot; means a charge for a violation of § <a href="/vacode/18.2-346/">18.2-346</a> or <a href="/vacode/18.2-347/">18.2-347</a>.</p><p>&quot;Victim of sex trafficking&quot; means any person charged with a qualifying offense in the Commonwealth who committed such offense as a direct result of being solicited, invited, recruited, encouraged, forced, intimidated, or deceived by another to engage in acts of prostitution or unlawful sexual intercourse for money or its equivalent, as described in § <a href="/vacode/18.2-346/">18.2-346</a>, regardless of whether any other person has been charged or convicted of an offense related to the sex trafficking of such person.</p><p>B. It is an affirmative defense to prosecution of a qualifying offense if at the time of the offense leading to such charge, such person was a victim of sex trafficking and (i) was coerced to engage in the offense through the use of force or intimidation or (ii) such offense was committed at the direction of another person other than the individual with whom the person engaged in the acts of prostitution or unlawful sexual intercourse for such money or its equivalent.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0334" target="_blank" rel="noreferrer">334</a>.</p>',6),r=[n];function f(s,c,d,h,u,l){return t(),i("div",null,r)}const g=e(a,[["render",f]]);export{p as __pageData,g as default};
