import{_ as e,o as i,c as o,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-370.1 Taking indecent liberties with child by person in custodial or supervisory relationship; penalties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-370.1.md","filePath":"vacode/18.2-370.1.md"}'),a={name:"vacode/18.2-370.1.md"},r=t('<h1 id="§-18-2-370-1-taking-indecent-liberties-with-child-by-person-in-custodial-or-supervisory-relationship-penalties" tabindex="-1">§ 18.2-370.1 Taking indecent liberties with child by person in custodial or supervisory relationship; penalties. <a class="header-anchor" href="#§-18-2-370-1-taking-indecent-liberties-with-child-by-person-in-custodial-or-supervisory-relationship-penalties" aria-label="Permalink to &quot;§ 18.2-370.1 Taking indecent liberties with child by person in custodial or supervisory relationship; penalties.&quot;">​</a></h1><p>A. Any person 18 years of age or older who, except as provided in § <a href="http://law.lis.virginia.gov/vacode/18.2-370/" target="_blank" rel="noreferrer">18.2-370</a>, maintains a custodial or supervisory relationship over a child under the age of 18 and is not legally married to such child and such child is not emancipated who, with lascivious intent, knowingly and intentionally (i) proposes that any such child feel or fondle the sexual or genital parts of such person or that such person feel or handle the sexual or genital parts of the child; or (ii) proposes to such child the performance of an act of sexual intercourse, anal intercourse, cunnilingus, fellatio, or anilingus or any act constituting an offense under § <a href="http://law.lis.virginia.gov/vacode/18.2-361/" target="_blank" rel="noreferrer">18.2-361</a>; or (iii) exposes his or her sexual or genital parts to such child; or (iv) proposes that any such child expose his or her sexual or genital parts to such person; or (v) proposes to the child that the child engage in sexual intercourse, sodomy or fondling of sexual or genital parts with another person; or (vi) sexually abuses the child as defined in subdivision 6 of § <a href="http://law.lis.virginia.gov/vacode/18.2-67.10/" target="_blank" rel="noreferrer">18.2-67.10</a> is guilty of a Class 6 felony.</p><p>B. Any person who is convicted of a second or subsequent violation of this section is guilty of a Class 5 felony, provided that (i) the offenses were not part of a common act, transaction or scheme; (ii) the accused was at liberty as defined in § <a href="http://law.lis.virginia.gov/vacode/53.1-151/" target="_blank" rel="noreferrer">53.1-151</a> between each conviction; and (iii) it is admitted, or found by the jury or judge before whom the person is tried, that the accused was previously convicted of a violation of this section.</p><p>1982, c. 521; 1986, c. 503; 1991, c. 517; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0840" target="_blank" rel="noreferrer">840</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0185" target="_blank" rel="noreferrer">185</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0794" target="_blank" rel="noreferrer">794</a>.</p>',4),n=[r];function s(l,c,h,d,p,u){return i(),o("div",null,n)}const v=e(a,[["render",s]]);export{g as __pageData,v as default};
