import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 18.2-370.5 Offenses prohibiting entry onto school or other property; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-370.5.md","filePath":"vacode/18.2-370.5.md"}'),i={name:"vacode/18.2-370.5.md"},n=t('<h1 id="§-18-2-370-5-offenses-prohibiting-entry-onto-school-or-other-property-penalty" tabindex="-1">§ 18.2-370.5 Offenses prohibiting entry onto school or other property; penalty. <a class="header-anchor" href="#§-18-2-370-5-offenses-prohibiting-entry-onto-school-or-other-property-penalty" aria-label="Permalink to &quot;§ 18.2-370.5 Offenses prohibiting entry onto school or other property; penalty.&quot;">​</a></h1><p>A. Every adult who is convicted of a Tier III offense, as defined in § <a href="/vacode/9.1-902/">9.1-902</a>, shall be prohibited from entering or being present (i) during school hours, and during school-related or school-sponsored activities upon any property he knows or has reason to know is a public or private elementary or secondary school or child day center property; (ii) on any school bus as defined in § <a href="/vacode/46.2-100/">46.2-100</a>; or (iii) upon any property, public or private, during hours when such property is solely being used by a public or private elementary or secondary school for a school-related or school-sponsored activity.</p><p>B. The provisions of clauses (i) and (iii) of subsection A shall not apply to such adult if (i) he is a lawfully registered and qualified voter, and is coming upon such property solely for purposes of casting his vote; (ii) he is a student enrolled at the school; or (iii) he has obtained a court order pursuant to subsection C allowing him to enter and be present upon such property, has obtained the permission of the school board or of the owner of the private school or child day center or their designee for entry within all or part of the scope of the lifted ban, and is in compliance with such school board&#39;s, school&#39;s or center&#39;s terms and conditions and those of the court order.</p><p>C. Every adult who is prohibited from entering upon school or child day center property pursuant to subsection A may after notice to the attorney for the Commonwealth and either (i) the proprietor of the child day center, (ii) the Superintendent of Public Instruction and the chairman of the school board of the school division in which the school is located, or (iii) the chief administrator of the school if such school is not a public school, petition the circuit court in the county or city where the school or child day center is located for permission to enter such property. The court shall direct that the petitioner shall cause notice of the time and place of the hearing on his petition to be published once a week for two successive weeks in a newspaper meeting the requirements of § <a href="/vacode/8.01-324/">8.01-324</a>. The newspaper notice shall contain a provision stating that written comments regarding the petition may be submitted to the clerk of court at least five days prior to the hearing. For good cause shown, the court may issue an order permitting the petitioner to enter and be present on such property, subject to whatever restrictions of area, reasons for being present, or time limits the court deems appropriate.</p><p>D. A violation of this section is punishable as a Class 6 felony.</p><p>2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0284" target="_blank" rel="noreferrer">284</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0370" target="_blank" rel="noreferrer">370</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0781" target="_blank" rel="noreferrer">781</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0402" target="_blank" rel="noreferrer">402</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0648" target="_blank" rel="noreferrer">648</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0796" target="_blank" rel="noreferrer">796</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0855" target="_blank" rel="noreferrer">855</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0688" target="_blank" rel="noreferrer">688</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0829" target="_blank" rel="noreferrer">829</a>.</p>',6),a=[n];function s(h,l,c,p,f,d){return o(),r("div",null,a)}const b=e(i,[["render",s]]);export{g as __pageData,b as default};
