import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 18.2-371.3 Tattooing or body piercing of minors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-371.3.md","filePath":"vacode/18.2-371.3.md"}'),t={name:"vacode/18.2-371.3.md"},n=i('<h1 id="§-18-2-371-3-tattooing-or-body-piercing-of-minors" tabindex="-1">§ 18.2-371.3 Tattooing or body piercing of minors. <a class="header-anchor" href="#§-18-2-371-3-tattooing-or-body-piercing-of-minors" aria-label="Permalink to &quot;§ 18.2-371.3 Tattooing or body piercing of minors.&quot;">​</a></h1><p>No person shall tattoo or perform body piercing for hire or consideration on a person less than eighteen years of age, knowing or having reason to believe such person is less than eighteen years of age except (i) in the presence of the person&#39;s parent or guardian, or (ii) when done by or under the supervision of a medical doctor, registered nurse or other medical services personnel licensed pursuant to Title 54.1 in the performance of their duties.</p><p>In addition, no person shall tattoo or perform body piercing on any client unless he complies with the Centers for Disease Control and Prevention&#39;s guidelines for &quot;Universal Blood and Body Fluid Precautions&quot; and provides the client with the following disclosure:</p><ol><li><p>Tattooing and body piercing are invasive procedures in which the skin is penetrated by a foreign object.</p></li><li><p>If proper sterilization and antiseptic procedures are not followed by tattoo artists and body piercers, there is a risk of transmission of bloodborne pathogens and other infections, including, but not limited to, human immunodeficiency viruses and hepatitis B or C viruses.</p></li><li><p>Tattooing and body piercing may cause allergic reactions in persons sensitive to dyes or the metals used in ornamentation.</p></li><li><p>Tattooing and body piercing may involve discomfort or pain for which appropriate anesthesia cannot be legally made available by the person performing the tattoo or body piercing unless such person holds the appropriate license from a Virginia health regulatory board.</p></li></ol><p>A person who violates this section is guilty of a Class 1 misdemeanor.</p><p>For the purposes of this section:</p><p>&quot;Body-piercing&quot; means the act of penetrating the skin to make a hole, mark, or scar, generally permanent in nature. &quot;Body piercing&quot; does not include the use of a mechanized, presterilized ear-piercing system that penetrates the outer perimeter or lobe of the ear or both.</p><p>&quot;Tattoo&quot; means to place any design, letter, scroll, figure, symbol or any other mark upon or under the skin of any person with ink or any other substance resulting in the permanent coloration of the skin, including permanent make-up or permanent jewelry, by the aid of needles or any other instrument designed to touch or puncture the skin.</p><p>1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0586" target="_blank" rel="noreferrer">586</a>; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0270" target="_blank" rel="noreferrer">270</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0692" target="_blank" rel="noreferrer">692</a>.</p>',9),a=[n];function s(p,l,c,d,h,g){return o(),r("div",null,a)}const m=e(t,[["render",s]]);export{f as __pageData,m as default};
