import{_ as e,o as i,c as r,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 18.2-374.1:1 Possession, reproduction, distribution, solicitation, and facilitation of child pornography; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-374.1:1.md","filePath":"vacode/18.2-374.1:1.md"}'),o={name:"vacode/18.2-374.1:1.md"},t=a('<h1 id="§-18-2-374-1-1-possession-reproduction-distribution-solicitation-and-facilitation-of-child-pornography-penalty" tabindex="-1">§ 18.2-374.1:1 Possession, reproduction, distribution, solicitation, and facilitation of child pornography; penalty. <a class="header-anchor" href="#§-18-2-374-1-1-possession-reproduction-distribution-solicitation-and-facilitation-of-child-pornography-penalty" aria-label="Permalink to &quot;§ 18.2-374.1:1 Possession, reproduction, distribution, solicitation, and facilitation of child pornography; penalty.&quot;">​</a></h1><p>A. Any person who knowingly possesses child pornography is guilty of a Class 6 felony.</p><p>B. Any person who commits a second or subsequent violation of subsection A is guilty of a Class 5 felony.</p><p>C. Any person who knowingly (i) reproduces by any means, including by computer, sells, gives away, distributes, electronically transmits, displays, purchases, or possesses with intent to sell, give away, distribute, transmit, or display child pornography or (ii) commands, entreats, or otherwise attempts to persuade another person to send, submit, transfer or provide to him any child pornography in order to gain entry into a group, association, or assembly of persons engaged in trading or sharing child pornography shall be punished by not less than five years nor more than 20 years in a state correctional facility. Any person who commits a second or subsequent violation under this subsection shall be punished by a term of imprisonment of not less than five years nor more than 20 years in a state correctional facility, five years of which shall be a mandatory minimum term of imprisonment. The mandatory minimum terms of imprisonment prescribed for violations of this section shall be served consecutively with any other sentence.</p><p>D. Any person who intentionally operates an Internet website for the purpose of facilitating the payment for access to child pornography is guilty of a Class 4 felony.</p><p>E. All child pornography shall be subject to lawful seizure and forfeiture pursuant to § <a href="/vacode/19.2-386.31/">19.2-386.31</a>.</p><p>F. For purposes of this section it may be inferred by text, title or appearance that a person who is depicted as or presents the appearance of being less than 18 years of age in sexually explicit visual material is less than 18 years of age.</p><p>G. Venue for a prosecution under this section may lie in the jurisdiction where the unlawful act occurs, where the alleged offender resides, or where any child pornography is produced, reproduced, found, stored, received, or possessed in violation of this section.</p><p>H. The provisions of this section shall not apply to any such material that is possessed for a bona fide medical, scientific, governmental, law-enforcement, or judicial purpose by a physician, psychologist, scientist, attorney, employee of the Department of Social Services or a local department of social services, employee of a law-enforcement agency, judge, or clerk and such person possesses such material in the course of conducting his professional duties as such.</p><p>1992, c. 745; 1993, c. 853; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0511" target="_blank" rel="noreferrer">511</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0659" target="_blank" rel="noreferrer">659</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0935" target="_blank" rel="noreferrer">935</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0938" target="_blank" rel="noreferrer">938</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0759" target="_blank" rel="noreferrer">759</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0823" target="_blank" rel="noreferrer">823</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0379" target="_blank" rel="noreferrer">379</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0399" target="_blank" rel="noreferrer">399</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0416" target="_blank" rel="noreferrer">416</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0369" target="_blank" rel="noreferrer">369</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0761" target="_blank" rel="noreferrer">761</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0291" target="_blank" rel="noreferrer">291</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0428" target="_blank" rel="noreferrer">428</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0096" target="_blank" rel="noreferrer">96</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0489" target="_blank" rel="noreferrer">489</a>.</p>',10),n=[t];function s(l,c,p,h,f,g){return i(),r("div",null,n)}const y=e(o,[["render",s]]);export{u as __pageData,y as default};
