import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-386.2 Unlawful dissemination or sale of images of another; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-386.2.md","filePath":"vacode/18.2-386.2.md"}'),s={name:"vacode/18.2-386.2.md"},i=t('<h1 id="§-18-2-386-2-unlawful-dissemination-or-sale-of-images-of-another-penalty" tabindex="-1">§ 18.2-386.2 Unlawful dissemination or sale of images of another; penalty. <a class="header-anchor" href="#§-18-2-386-2-unlawful-dissemination-or-sale-of-images-of-another-penalty" aria-label="Permalink to &quot;§ 18.2-386.2 Unlawful dissemination or sale of images of another; penalty.&quot;">​</a></h1><p>A. Any person who, with the intent to coerce, harass, or intimidate, maliciously disseminates or sells any videographic or still image created by any means whatsoever that depicts another person who is totally nude, or in a state of undress so as to expose the genitals, pubic area, buttocks, or female breast, where such person knows or has reason to know that he is not licensed or authorized to disseminate or sell such videographic or still image is guilty of a Class 1 misdemeanor. For purposes of this subsection, &quot;another person&quot; includes a person whose image was used in creating, adapting, or modifying a videographic or still image with the intent to depict an actual person and who is recognizable as an actual person by the person&#39;s face, likeness, or other distinguishing characteristic.</p><p>B. If a person uses services of an Internet service provider, an electronic mail service provider, or any other information service, system, or access software provider that provides or enables computer access by multiple users to a computer server in committing acts prohibited under this section, such provider shall not be held responsible for violating this section for content provided by another person.</p><p>C. Venue for a prosecution under this section may lie in the jurisdiction where the unlawful act occurs or where any videographic or still image created by any means whatsoever is produced, reproduced, found, stored, received, or possessed in violation of this section.</p><p>D. The provisions of this section shall not preclude prosecution under any other statute.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0399" target="_blank" rel="noreferrer">399</a>; 2019, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0515" target="_blank" rel="noreferrer">515</a>.</p>',6),r=[i];function n(l,c,h,p,d,u){return o(),a("div",null,r)}const g=e(s,[["render",n]]);export{m as __pageData,g as default};
