import{_ as e,o as a,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 18.2-403.3 Offenses involving animals — Class 4 misdemeanors.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-403.3.md","filePath":"vacode/18.2-403.3.md"}'),n={name:"vacode/18.2-403.3.md"},t=i('<h1 id="§-18-2-403-3-offenses-involving-animals-—-class-4-misdemeanors" tabindex="-1">§ 18.2-403.3 Offenses involving animals — Class 4 misdemeanors. <a class="header-anchor" href="#§-18-2-403-3-offenses-involving-animals-—-class-4-misdemeanors" aria-label="Permalink to &quot;§ 18.2-403.3 Offenses involving animals — Class 4 misdemeanors.&quot;">​</a></h1><p>The following unlawful acts and offenses against animals shall constitute and be punished as a Class 4 misdemeanor:</p><ol><li><p>Violation of § <a href="/vacode/3.2-6566/">3.2-6566</a> pertaining to interference of agents charged with preventing cruelty to animals.</p></li><li><p>Violation of § <a href="/vacode/3.2-6573/">3.2-6573</a> pertaining to shooting pigeons.</p></li><li><p>Violation of § <a href="/vacode/3.2-6554/">3.2-6554</a> pertaining to disposing of the body of a dead companion animal.</p></li><li><p>Unless otherwise punishable under subsection B of § <a href="/vacode/3.2-6587/">3.2-6587</a>, violation of ordinances passed pursuant to §§ <a href="/vacode/3.2-6522/">3.2-6522</a> and <a href="/vacode/3.2-6525/">3.2-6525</a> pertaining to rabid dogs and preventing the spread of rabies and the running at large of vicious dogs.</p></li><li><p>Violation of an ordinance passed pursuant to § <a href="/vacode/3.2-6539/">3.2-6539</a> requiring dogs to be on a leash.</p></li><li><p>Failure by any person to secure and exhibit the permits required by § <a href="/vacode/29.1-422/">29.1-422</a> pertaining to field trails, night trails and foxhounds.</p></li><li><p>Diseased dogs. — For the owner of any dog with a contagious or infectious disease, other than rabies, to permit such dog to stray from his premises if such disease is known to the owner.</p></li><li><p>License application. — For any person to make a false statement in order to secure a dog or cat license to which he is not entitled.</p></li><li><p>License tax. — For any dog or cat owner to fail to pay any license tax required by subsection A or C of § <a href="/vacode/3.2-6530/">3.2-6530</a> within one month after the date when it is due. In addition, the court may order confiscation and the proper disposition of the dog or cat.</p></li><li><p>Concealing a dog or cat. — For any person to conceal or harbor any dog or cat on which any required license tax has not been paid.</p></li><li><p>Removing collar and tag. — For any person, except the owner or custodian, to remove a legally acquired license tag from a dog or cat without the permission of the owner or custodian.</p></li><li><p>Violation of § <a href="/vacode/3.2-6503/">3.2-6503</a> pertaining to care of animals by owner.</p></li></ol><p>1984, c. 492; 1993, cc. 174, 817; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0559" target="_blank" rel="noreferrer">559</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0567" target="_blank" rel="noreferrer">567</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1183" target="_blank" rel="noreferrer">1183</a>.</p>',4),r=[t];function s(l,c,d,p,f,h){return a(),o("div",null,r)}const u=e(n,[["render",s]]);export{m as __pageData,u as default};
