import{_ as t,o as n,c as i,z as e,a as o}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 18.2-435 Giving conflicting testimony on separate occasions as to same matter; indictment; sufficiency of evidence.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/18.2-435.md","filePath":"vacode/18.2-435.md"}'),a={name:"vacode/18.2-435.md"},s=e("h1",{id:"§-18-2-435-giving-conflicting-testimony-on-separate-occasions-as-to-same-matter-indictment-sufficiency-of-evidence",tabindex:"-1"},[o("§ 18.2-435 Giving conflicting testimony on separate occasions as to same matter; indictment; sufficiency of evidence. "),e("a",{class:"header-anchor",href:"#§-18-2-435-giving-conflicting-testimony-on-separate-occasions-as-to-same-matter-indictment-sufficiency-of-evidence","aria-label":'Permalink to "§ 18.2-435 Giving conflicting testimony on separate occasions as to same matter; indictment; sufficiency of evidence."'},"​")],-1),c=e("p",null,"It shall likewise constitute perjury for any person, with the intent to testify falsely, to knowingly give testimony under oath as to any material matter or thing and subsequently to give conflicting testimony under oath as to the same matter or thing. In any indictment for such perjury, it shall be sufficient to allege the offense by stating that the person charged therewith did, knowingly and with the intent to testify falsely, on one occasion give testimony upon a certain matter and, on a subsequent occasion, give different testimony upon the same matter. Upon the trial on such indictment, it shall be sufficient to prove that the defendant, knowingly and with the intent to testify falsely, gave such differing testimony and that the differing testimony was given on two separate occasions.",-1),r=e("p",null,"Code 1950, § 18.1-276; 1960, c. 358; 1975, cc. 14, 15.",-1),f=[s,c,r];function d(m,l,h,y,g,u){return n(),i("div",null,f)}const _=t(a,[["render",d]]);export{v as __pageData,_ as default};
